INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (698, '纳拉雷克斯又陷入了沉睡!$B$B很久以前，纳拉雷克斯曾满怀着进入翡翠梦境、让贫瘠之地重新变成茂密森林的伟大理想而来到了这处洞穴中。但他的执着却逐渐消退了，而他的思想也被脑海蜿蜒的幻象所侵染。$B$B现在，那些奇怪的爬虫正从他的梦境中爬到这片土地上来，我们必须把他从梦魇中唤醒过来，否则他就将永远失落於其中，并继续给这片大地带来邪恶的力量!$B$B不过，只有杀死他那些堕落的仆人─毒牙之王，我才能开始进行唤醒仪式。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (699, '毒牙之王死了!我们还有希望!$B$B然而，整个唤醒仪式是非常复杂的，我无法独自完成它。$B$B我现在就开始着手完成所有必要的准备工作，但与此同时，我需要你保护好我。从纳拉雷克斯的梦魇中召唤出的怪物确实非常难缠。如果没有你的帮助，这个任务对我来说会非常可怕。$B$B当你准备好要保护我的时候，就过来跟我说一声，我会带你到纳拉雷克斯的房间去。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
