INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (798, '终於!终於从这破牢房里逃出来了!$B$B祈伦托将我从达拉然派到这个银松森林里来，要我调查阿鲁高和他的亡灵仆从们的活动，可我们却一点也不知道那个巫师已经完全失去了理智。$B$B我必须立刻把这个消息汇报上去!可在我离开之前，让我先把庭院门上的魔法锁给打开。$B$B既然你能击败雷希戈尔，或许也能从前面的层层埋伏中杀出一条血路……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (799, '终於!终於有人把我从这破牢房里给救出来了!$B$B高阶执行官哈德瑞克大人派我们来这儿收集这座要塞里的资讯，以便制定一个能永久消灭铲除阿鲁高的计画。$B$B但那个老不死的巫师还真有点能耐，我们很快就被他的魔法结界给发现了。我被抓来关在这里，不过文森特可就没这么走运了。$B$B我得马上回去向哈德瑞克大人汇报。首先我会把庭院里的锁给你打开，或许你这么强悍的$C能从前面的一大堆伏兵里杀出一条血路……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (818, '我以前曾经追寻过暮光之锤的道路。为了找到复活上古诸神的方法，我只身深入这座巢穴，想要找到从那个该死的恶魔阿库麦尔身上抽取能量的方法。$B$B在看到无数无辜人白白地牺牲之后，我慢慢恢复了理智。$B$B我被困住了一段时间，但是现在我自由了!不幸的是，这里离我的家乡卡兹莫丹太远了!$B$B勇敢的冒险者，我现在可以将你传送至一个特别的地方，然后回来干掉阿库麦尔。如果你愿意的话，我现在就送你到达纳苏斯去。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
