INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (943, '是的，我见过你要找的萨满。上次我见到她的时候，她正往西边去。她隐居在钢鬃部族上方的小山上。如果你够聪明的话，就可以找到一条通往小屋的山路，就在北边不远的地方。那条路可以让你避开野猪人，直接找到她。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
