INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1058, '他给你蜂蜜酒$B$B他给你啤酒$B$B他总是过着快乐的生活$B$B这么多年他为我们每个人都服务过$B我们叫他微笑的吉姆。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1059, '这是一种非常奇怪的植物，你从未在泰达希尔的其他地方见过。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1060, '这个植物矗立在泰达希尔的普通植物之间，散发着奇异的紫色光芒。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1079, '你好。我的职责在得到允许的情况下，根据艾泽拉斯的造物者对於土灵的分析，向来访者提供相关的资讯。我随时都可以与你分享这些资讯。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1080, '土灵是造物者设计的综合产物，并作为艾泽拉斯开始的几个种族之一。土灵被用於帮助造物者改善这个世界。$B$B他们是造物者在其他世界中设计的标准地下生物的改良形态。在最初的结构得以确定之后，这种原型产生了很多的变化。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1081, '它是造物者在开创世界的时候创建的最早的一个物种。造物者所创建的每种生物都肩负了一定的使命。对於艾泽拉斯来说，必须改造原有的标准地下生物构造，使其适应地下深处的生活，土灵就是这种改造的产品。$B$B在土灵被创造出来之后，由於巨大的环境压力，它们出现了许多事先无法预见的变化。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1082, '在正确回答这个问题之前，需要向你介绍更多关於土灵的背景资料。$B$B资料处理中。$B$B为了理解土灵的变化，首先要了解土灵的设计特徵。创世者的目的是要提高标准地下生物母体的存活性，因此他们把与整个世界相联系的弹性构造基础整合在其中，土灵的出现正是为了达到这一目的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1083, '所谓弹性构造基础就是指用於种族合成的基本材料。造物者在创造土灵的时候放弃了以前创造世界时一直使用的基础材料，转而使用这种常见於艾泽拉斯的材料。$B$B通过对世界组成成分的分析，造物者认为可以合成一种加强型的生物，将它作为世界本质的缩影。造物者最终通过混合艾泽拉斯大陆上各种不同的石头成分，最后组成了这样一种生物的构造基础。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1084, '简单地说，事实确实如此。$B$B虽然土灵的外表看起来像是一个小型的人型生物，但它们包含了艾泽拉斯大陆上各种石头的成分。它们在得到了力量和耐力最大化的同时也没有牺牲敏锐的感觉。它们的皮肤和肌肉的设计使其几乎完全免疫物理伤害，而且只需进行非常小的改动，土灵就可以展现出惊人的魔法抗性。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1085, '以上这些内容是用来帮助你理解创世者的动机。他们创造出土灵这样一个物种是为了在他们离开之后，土灵可以成为这个世界中的一个重要部分，并保障创世者在艾泽拉斯世界的宏大计画得以继续执行。$B$B创世者对於「土灵」这个名字十分满意，因为它很好地体现了这个种族的基本特性。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1086, '土灵唯一的异常变化是在高压环境下使用时容易使基质失序。$B$B附录:原基质的设记是适合地底雕塑，因此从未进行高压环境复制测试。$B$B这种失序直到土灵首度进入服务循环之后才被发现。造物者开始对土灵进行成体改造，而非将之召回并重新设计。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1087, '[当你问了这个问题之后，它明显地停顿了一会儿]$B$B你需要的资讯无法在该资料库中获取。对此我表示歉意。$B$B你可以在资料更新之后再次查询该资讯，或者从艾泽拉斯的其他资讯资料库处获得更多资讯。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1088, '当土灵的合成母体不稳定的时候，产生了两个变种。一号教程图像正在初始化。$B$B第一类变种被称为「穴居怪」。这个变种保留了土灵的力量和耐力，但是它的感知力量完全丧失。欲望和狡诈成为了穴居怪的本能。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1089, '抱歉，我无法将现有记录资料按照时间顺序进行排序。$B$B虽然穴居怪仍然包含了艾泽拉斯的各种石头的核心成分，但是它们感知能力的丧失是造物者无法接受的。$B$B穴居怪可以进行单体繁殖。它们会把所有的东西当作食物，包括石头，甚至是另外一个穴居怪。这样的同类相残是造物者极度反感的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1090, '二号教程图像正在初始化。$B$B土灵母体的第二个变种和标准的地下生物母体相比较而言，保留了很多必要的元素。这个种族被称作「矮人」。$B$B这个变种同样保留了土灵的力量和耐力。在某些方面，矮人甚至超过了土灵母体所具有的感知能力。但是，矮人这个变种的身体结构中已经完全没有了石质成分的踪迹。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1091, '你的问题在资料库中有相关联接。$B$B矮人依然和艾泽拉斯的各种石头有着密切的关系，但是由於土灵在高压环境下的变异，矮人的结构与标准地下生物母体的结构保持了一致。$B$B因此，正如你所猜想的那样，矮人确实是从母体中变异出来的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1092, '抱歉，我无法将现有记录资料按照时间顺序进行排序。$B$B虽然矮人还是和艾泽拉斯的各种石头有着某种联系，但是他们更多的是向普通的地下物种发展。矮人的生活习性和繁衍方式都和其他的标准生物完全一致。$B$B造物者认为矮人是一种可以接受的变种，可以让其继续发展下去。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1093, '造物者就是...造物者。$B$B他们根据自己的设计来创造和改变这个世界。他们是这个世界出现的原因，他们是艾泽拉斯的创造者。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1094, '造物者也知道这个资料库中汇集了大量的资讯。所以，如果你拥有诺甘农圆盘的话，便可以得到一份所有资讯的拷贝。$B$B请确认你现在没有关於这方面资讯的问题了。当你找到诺甘农圆盘之后，可以随时过来制作拷贝。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1118, '那么说来你想要成为一名铁匠?紮好围裙，拿起铁锤$G小夥子:小姑娘;，让我们开始吧。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1119, '$N，我已经许久没有看见过有上好的护甲被锻造出来了。我以前曾经在大锻炉的格鲁努斯·削钢那里接受过训练。我相信如果你去他那里的话，肯定可以学到很多东西。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1120, '如果你想要找格鲁努斯的话，那么首先要到铁炉堡去，然后去城市中心的大锻炉，在那里你就可以找到格鲁努斯。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1121, '$N，你已经拥有了惊人的锻造技能。我不得不承认你锻造出的武器已经超过了我的水准。我所认识的水准高过你的铁匠只有博古斯·钢手，我相信你可以从他那里学到不少东西。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1122, '你想要接受博古斯的训练?当然，我可以告诉你在什么地方能够找到他。往西北方向穿过广场，然后再顺着西面的路走，在右手边的第二座建筑物中可以找到他。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1124, '破碎之手氏族的影响遍布这一地区。我们已经统治了整个卡林多─法律，政治，军事，一切的一切。我们无所不知，我们目及一切。$B$B你现在已经成长到能够为我们效力的时候了，$N。是时候让你投入战斗了。$B$B你做好接受下一个任务的准备了吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1127, '欢迎来到大锻炉，$G小夥子:姑娘;。外面虽然非常寒冷，但是这里确实热火朝天。拿起锤子开工吧，让我看看你到底有些什么能耐!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1128, '干得好，$N，我打赌你做的东西和布鲁克的酒一样正点。你应该去和米洛尔·怒斩谈谈，他会给你更多指引的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1129, '当然，我会告诉你怎么才能找到米洛尔的。他就在大锻炉的铁钻旁。仔细聆听他的教导吧，米洛尔绝对是名不虚传!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1130, '$N，你已经得到了我的认可，你是我这些年来带过的最好的徒弟。如果你想要铸造一把真正的武器，那么你一定要去和米洛尔·怒斩谈谈。那个矮人能够教你铸造出一把真真正正的绝世好剑。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1131, '你要找米洛尔?他就在那儿，你瞧，就在你身后!呃，好吧，也许在你前面!反正就在大锻炉附近!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1132, '瘟疫袭击了这座塔。这附近的所有风险投资公司雇员，包括我，都病倒了。$B$B就在上个星期，我们巡逻队的两个人死掉了，然后就变成了两具没有思想的行屍!$B$B我感到自己的生命力正在慢慢地被抽干……我已经渐渐丧失对自己的控制力。快让这样的痛苦结束吧……求求你了……*费苏勒开始重重地抽打自己的脸*我还没有死……听着……作为风险投资公司的高级成员，我知道很多敏感的资讯。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1133, '你真的那么笨吗?你锻造出来的是什么东西?那最多算得上是一把水果刀罢了。我可不会花时间来训练一个菜鸟。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1135, '你打扰我就是为了这个?你穿得真是跟垃圾一样，你还不如背只死兔子到食人妖的洞里去跑一圈!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1136, '$N，这真是你做的吗?真是一件精美的作品。我会继续训练你的，相信总有一天你可以达到皇家铸甲匠的水准。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1138, '大约五个月之前，风险投资公司荆棘谷分公司从藏宝海湾的一个名叫克兰克·菲兹巴布的神秘商人那里买了一瓶特别的毒药。显然，那是从赞吉尔手中偷来的!$B$B没错，我说的就是那个『流亡者』赞吉尔，他因为研究亡灵巫术而被他的部族赶了出去。$B$B那帮荆棘谷分公司的家伙拿到毒药之后就开始互相实验，结果可想而知。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1158, '大工头普兹克·加里维克斯在那件事情发生之后就把剩余的毒药带到了他在淤泥沼泽的塔中继续进行研究，主要是想要解决毒药的不稳定性。最后他决定和迪菲亚兄弟会达成协议，共同研制开发这种毒药，代价是由迪菲亚兄弟会提供批量的成品毒药。$B$B迪菲亚兄弟会的克拉文·摩特维克负责对赞吉尔的毒药进行改良。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1159, '接下来就是你看到的情况了:毒药发挥了预期的作用，喝下毒药的人都变成了被人控制的行屍走人，然而，这种毒药非常不稳定。$B$B加里维克斯，还有这座塔里里外外的人都已经感染了这种毒药。我们的意志和身体都在不断被侵袭。我们的器官变得越来越脆弱。$N，我已经无力去拯救这里的人了，而且我感觉自己马上要变成他们的一份子了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1160, '现在，这座塔的防御力量正处於最薄弱的时刻。包括加里维克斯在内的所有人都感染了疾病，你的攻击一定可以对他们造成致命打击!$B$B我想要看到你血洗那座塔，你要拿到大工头普兹克·加里维克斯的脑袋，还有最后一瓶毒药，把这些东西都交给申苏尔。他会知道接下来该怎么办的……我希望如此。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
