INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (1646, '', 'The Gordunni are a large ogre tribe that has control over considerable portions of forests in Feralas. Their dark magic seeps into the elements around them, damaging both the environment and the creatures that inhabit it.$B$BIf you do decide to travel to Feralas, please be mindful of these ogres -- they are quite nasty and many are able to wield powerful dark magic. ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
