INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2394, '你必须耐心一些，$N。我的间谍机器人正在进行非常精密的操作，马上就帮你搞定。$B$B哦，帮我个忙，尽量不要让莱斯科瓦看到你……我们要伏击他。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
