INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2433, '我亲眼看到的!绝对是有始以来最大的龙虾人，比山丘巨人的拳头还大!它用自己的钳子爬上了双塔山的其中一座。我不知道你要怎样才能上去那，但那野兽对你来说一定价值连城。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2434, '当然，$N。看起来你是个值得信任的$R!我在一里开外就发现了……$B$B总之，说正经事。我需要一株灵根草才能制作视灵药剂。药剂所需的其他成分都是小菜一碟，但是灵根草就不是那么容易得到了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2473, '快动手吧，肮脏的$R。$B$B<卡兰低下头，露出他粗壮的脖子。>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
