INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2642, '氦弹点火和爆炸的过程是没有危险的。跟船只相比，这宝贝能更为快速安全地将你送达幽暗城，途中的景色真是惊心动魄。说到这，在地精飞艇上是不允许吸烟的，旅途中也不允许使用火的法术。', '氦弹点火和爆炸的过程是没有危险的。跟船只相比，这宝贝能更为快速安全地将你送达幽暗城，途中的景色真是惊心动魄。说到这，在地精飞艇上是不允许吸烟的，旅途中也不允许使用火的法术。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
