INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2644, '我兄弟说我们能在这条商贸路线上赚到更多的钱的，但是他没告诉我要独自应对。要是你乘坐地精飞艇沿着这条路返回的话，你能帮我带点吃的吗?在奥格玛，有人出售最好的肉类，当然我还能以那些苟延残喘的动物为食的。', '我兄弟说我们能在这条商贸路线上赚到更多的钱的，但是他没告诉我要独自应对。要是你乘坐地精飞艇沿着这条路返回的话，你能帮我带点吃的吗?在奥格玛，有人出售最好的肉类，当然我还能以那些苟延残喘的动物为食的。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
