INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2734, '', 'Have you seen all the wonderful things here in Ungoro Crater, $N? It\'s simply amazing. Not as amazing as some of the things I can make with my skill at engineering, but for it being all natural here... it\'s not too bad.$B$BMy uncle Remtravel loves old stuff like this place. I\'m following in his footsteps kinda--I want to discover new things from lost cultures too!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2735, '', 'Hmm, I don\'t have any mithril casings. I\'m all out. Is that all you need to get to fix A-Me? Oh, that would be fantastic!$B$BMithril casings aren\'t too hard to find back in civilization. Just ask any skilled engineer to make one for you. I think it takes a few bars of mithril, but if you could help me out, I\'ll make sure you\'re well rewarded!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
