INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (2993, '它们都失踪了，$R。在天谴军团入侵奎尔萨拉斯的时候，精灵族的圣契散落到世界各地。$B$B现在我们得搜寻这些圣契。寻找其中的秘密─并恢复其中蕴含的力量。$B$B我在寻找由卡里尔·温萨鲁斯书写的圣契。如果你找到了圣契，请允许我吸收它的魔法力量，而为你我可以制作一种力量的融合物……当然，所有的事情都是有代价的。这项任务的风险极大，但是我给予的回报也是相当丰厚的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
