INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (3942, '承蒙月神的关照，我很高兴给你在整个泰达希尔找到的最好的坐骑，甚至是这世上最好的--黑豹。数年来，哨兵部队一直倚赖这些同伴的价值。$B$B抓紧时间，朋友，挑选一匹你满意的。我敢肯定你不会失望的。', '承蒙月神的关照，我很高兴给你在整个泰达希尔找到的最好的坐骑，甚至是这世上最好的--黑豹。数年来，哨兵部队一直倚赖这些同伴的价值。$B$B抓紧时间，朋友，挑选一匹你满意的。我敢肯定你不会失望的。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (3944, '嗯……我认为你体型过大，不能乘坐机械陆行鸟，$G先生:女士;。你很可能会压碎这些精美的机器!', '嗯……我认为你体型过大，不能乘坐机械陆行鸟，$G先生:女士;。你很可能会压碎这些精美的机器!', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (3945, '如果你是为获得快速而安全的产品而来，那么这儿就是你的目的地了!今年的机械陆行鸟模型已经到了，我将为你展示其性能!', '如果你是为获得快速而安全的产品而来，那么这儿就是你的目的地了!今年的机械陆行鸟模型已经到了，我将为你展示其性能!', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (3953, '当一个铁匠达到了你们专家工艺水准的时候，我会提供给他们一个选择。$B$B虽然没有要求你做出选择，但建议你最好还是做出抉择─毕竟，你想永远的当一名新手吗?是专家级的护甲锻造师和武器锻造师给我们军队提供补给，从而赢得战争的胜利并获得称赞!$B$B注意，$N。一旦做出了决定并完成了要求的任务--就没有回头路了。一个人只能沿一条路走到底。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (3954, '我是克拉索克，这些工匠的监督者。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
