INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4473, '很高兴见到你，$C。除非你和我的女主人玛加萨之间有直接交易关系，否则别找我说话。$B$B我帮她处理大部分的事务，让她能集中精力处理更重要的事情。或许在你拜见她之前我们可以谈谈。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
