INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4475, '你随时都可以跟我交谈，$N。你对玛加萨忠心耿耿。我想将来她会召见你的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4476, '别把自己当我的同辈来跟我讲话，$C。希瓦娜斯是我的女王，我听从她的命令。$B$B要是你不表现出适当的尊重，我敢说我们将为你在扭曲虚空选个更为合适的位置。$B$B但是要是你愿意帮助黑暗女王的话，我们便无话可说了……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4477, '在我眼中，部落的所有成员都是平等的，$N。我们的血液被恶魔玛诺洛斯污染了，人民承受着重负，如果不是为了学识和荣耀的话，我们几乎就快沦为天谴军团了。$B$B协助部落，让我们获得新生，这是你的职责。但是你也得明白什么时候应该对朋友同情，对敌人怜悯。$B$B好好理解。这是新生的部落，而不是缺乏自由意愿的恶魔军队。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4493, '由於我的身份问题，我并未对你说实话，我必须为此向你道歉。我必须确定你是值得信赖的─很少有人能无私地为一个年老的隐士提供帮助。$B$B你已经证明了自己是个正直崇高的人。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4494, '当我以叛国罪被放逐离开联盟过着流放的生活时，我的儿子泰兰还是个小孩。我待在这儿就是为了看着他长大，让他成为一个可敬的人。$B$B正如我期望的那样，他接替我的位置，成为玛登霍尔德的领主，但是命运是如此残酷，他加入了血色十字军的队伍。现在他成了十字军的高级统领，$R。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4495, '真的。血色十字军是白银之手骑士团做出的错误决策。$B$B你必须相信我，$R;泰兰是个好人。他需要指导。他必须记起……记起高贵和荣耀的含义。我知道那信念就在他心中，他知道他所做的是错误的。你帮助他好吗?帮助他记起来?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4513, '你要仔细听好了，别轻易相信或怀疑你所听到的一切。因为即使是朋友也可能将自己送上死路，$C。$B$B我们伟大的大酋长深知这一点，为了证明部落中有一些术士并不想从内部毁掉部落，或是为了获得权力，我自愿尽我所能帮助年轻的首领。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4516, '吴平的精力真令人惊奇。我看着他连续练习了数小时技巧。他用双手击打练习用的假人直到他的拳头沾满鲜血。我想不到还有谁比他还适合训练你的战斗技能。', '吴平的精力真令人惊奇。我看着他连续练习了数小时技巧。他用双手击打练习用的假人直到他的拳头沾满鲜血。我想不到还有谁比他还适合训练你的战斗技能。', '吴平的精力真令人惊奇。我看着他连续练习了数小时技巧。他用双手击打练习用的假人直到他的拳头沾满鲜血。我想不到还有谁比他还适合训练你的战斗技能。', '吴平的精力真令人惊奇。我看着他连续练习了数小时技巧。他用双手击打练习用的假人直到他的拳头沾满鲜血。我想不到还有谁比他还适合训练你的战斗技能。', '', '', '', '', '', '', '', '', '', '', '', '');
