INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4813, '啊……一名顾客!看看我最伟大的发明，贸易机器人1000型!它拥有你所需的一切商品……其中的一些稀有物品更是在卡林多的其他任何市场上都找不到……更不用说它的设计不支持任何讨价还价的废话了，因此你看到的价格就是你要支付的价格。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (4814, '毫无疑问，你是黑暗女王派来接受训练的又一个无能的白痴。$B$B<纳萨诺斯叹息着。>$B$B我每次都得把你们的屍体运回去给她，我想这次也不会有什么例外。$B$B但是，我并不会问什么，我只按照黑暗女王的吩咐办事。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
