INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (5734, '似乎黑龙军团的势力范围已经超越了燃烧平原，扩展到暴风城了!人们揭下了篡位者的面具并将他流放出境。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (5735, '真的。黑龙公主运用幻影和欺诈术将自己伪装成贵族法庭的成员。更糟糕的是，现在，她似乎已经控制了暴风城的大领主伯瓦尔·弗塔根。$B$B做出这样愚蠢的军事决定不足为奇。联盟军队最近的行动令我们头疼。现在我们知道，黑龙正试图分裂帝国─从内部分裂它。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (5736, '<萨尔笑了。>$B$B还有这么多要学的，$R。$B$B你太年轻了，记不得多年前的战争和流血。记不得我们的人民所承受的诅咒的重压。$B$B我们现在与联盟的形势很脆弱。小冲突和小规模战斗时常发生，但是仍有和平--我们的人民数千年来未有过的和平。你愿放弃这个并把我们再次投入这片混沌中去面对一个新的敌人吗?这个敌人在毁灭我们之前是不会停手的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (5737, '不，$N，你没有。我没找你茬，但是你跟年幼的我非常相似:冲动、叛逆、思维缺乏连贯性!这些特点在我身上已经荡然无存。$B$B悲伤够了。$R，我有个任务要你完成;或许你会在执行任务过程中丧命。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (5738, '<萨尔笑着。>$B$B了注意……现在专心些，$N。黑龙公主，奥妮克希亚，被逐出了她原本在暴风城一带的势力范围。她已经逃到她在尘泥沼泽的窝里。$B$B她不能生活在离我们的首都这么近的地方。我们必须先发制人!$B$B我想要消灭她，$N。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
