INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6178, '我被穆维里克这个鲁莽的傻子击中了。我担心要是不能将这东西送到基地的话，所有的事情都会失败的!必须阻止穆维里克和他的骑兵队!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6179, '真不敢相信…我在中央非军事区执行例行侦察任务时，事情变得一团糟。我看见部落空军指挥官穆维里克击倒了艾克曼和斯里多尔!怒不可遏的我，撇下我的僚机，把那个爱现自大的穆维里克打了下来。不幸的是，他在坠机之前击中了我的狮鹫兽，我也随之跌下。结局就是我被这些野蛮的家伙给活逮了。$B$B我必须返回基地!帮帮我!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6180, '当我的狮鹫兽坠落时，我听到了他们嘲弄的笑声，$C。$B$B「斯里多尔， 你太差劲了!」他们大声嚷道。$B$B没有人敢对斯里多尔那样说话!帮我离开这里，我会让他们知道我的厉害!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
