INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6949, '是的，我知道埃德萨拉斯。多年前，那儿曾是夜精灵的魔法要塞。$B$B你知道埃德萨拉斯吗，年轻人?也许有人会说这跟我目前的处境一致……但是我可不相信巧合这回事。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6950, '塞纳里奥议会的人已经了解埃德萨拉斯的近况了。我陷入僵局，不知如何是好。我很可能向那些体格强健、善於作战的人寻求帮助。$B$B但是，要对埃德萨拉斯采取行动，就要了解关於丰硕的果实的知识。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6951, '多年前，埃德萨拉斯是魔法师的集中地，居住在那些神圣的大厅中的人都能使用强大的魔法。米莉仙·瑟琳就是这样一名夜精灵─她是个自然学家，能将自然本身的力量灌输到她铸造的神器中。$B$B丰饶之果便是她的杰作……她利用特殊的月井制造出一种奇异的葡萄酒，具备保护和生长的效力。埃德萨拉斯在葡萄酒的滋养下日益繁华起来。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6952, '唉，事情似乎总是这样。和塞纳里奥议会的其他人一样，我也认为是米利的造物在先祖之战中毁掉了。但是我已经认识到，这种观点是错误的。$B$B丰硕的果实并未被摧毁……尽管现在它被称为恶魔之藤。它被埃德萨拉斯的恶魔领主『狂野变形者』奥兹恩污染了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6953, '我……我无法开始想像，$N。我只知道我不能让这件事发生。它会被摧毁的─就算不毁在我们对巨魔的防御上，也会毁在米莉仙·瑟琳的记忆上……米莉……$B$B我的朋友，这就是我希望你伸出援手的事情。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6954, '<德米提恩跪倒在地。>$B$B你已经找回了主人的牢笼！$B$B我期待这一天已经很久很久了，我的愿望终于实现了。$B$B必须解放我的主人！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
