INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6992, '你刚从地城出来吗?有什么东西给我的吗?', '', '你刚从地城出来吗?有什么东西给我的吗?', '', '你刚从地城出来吗?有什么东西给我的吗?', '', '你刚从地城出来吗?有什么东西给我的吗?', '', '如果罩衫是由顶部的摁扣固定的，那为什么还需要袢带呢?', '', '要是你的鼻涕在鼻子里结冰了怎么办?', '', '鱼不会口渴吗?', '', '如果你想要失败，但是却成功了，你做了什么?', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6993, '你的护甲看起来很破旧。你为此感到胆怯吗?', '', '你的护甲看起来很破旧。你为此感到胆怯吗?', '', '你的护甲看起来很破旧。你为此感到胆怯吗?', '', '你的护甲看起来很破旧。你为此感到胆怯吗?', '', '我的鼻子里有块石头。', '', '要是海龟没有壳的话，那不就成无家可归或是裸体啦?', '', '「啧啧」是什么意思?', '', '要是你的手套太大的话，还算是合手的手套吗?', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6995, '你离家还远着呢，$R。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6996, '你已经找到我啦。你是来学习镇压和控制之道的吧。$B$B我会教你更多的东西……你将学到牺牲。$B$B<戴奥咧嘴而笑。>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6997, '你得选择自己要召唤和驱使的恶魔。我们运用黑暗力量并为此付出巨大的代价。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (6998, '要是你还想继续前进的话，我们必须首先找人对付野兽。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7001, '卑微的家伙，在我面前鞠躬吧!你能感受到我那强大的力量。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7002, '', 'So you have come to Niby to learn the secrets of calling down an infernal servant from the Nether.$B$BSilence! Niby knows all!$B$BNiby is not so quick to part with such knowledge. First, you must assist Niby with a very important task.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7003, '', 'There is a powerful infernal named Kroshius northeast of this camp. It is why Niby is here. Kroshius is a greater infernal - an infernal that has lasted longer than expected. You see, nub, an infernal crashes down into the earth with great intensity to serve its master but this intensity fades fast. Once the blazing flames have died down, the infernal crumbles.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7004, '', 'Do not interrupt Niby, fool! I shall rend the flesh from your bones and use your skeletal remains as a coat rack. Now where was I? Oh yes, so, Kroshius lasted nearly ten times longer than any ordinary infernal. I am not sure why but I am confident that with enough study, I could find out.$B$BI need you to recover Kroshius\' Infernal Core.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7005, '', 'If I am to teach you the secrets of summoning an infernal servant, you must destroy Kroshius and bring me the infernal core from his burning remains. This, however, is not as easy as it sounds. Kroshius has all but burned out completely. You must reignite his corpse and raise him from the rubble. To do this, however, you must gather some components from other demonic entities.$B$BMy imp, Impsy, shall set you on your way.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7006, 'A Sulfuron ingot?!??! Impossible! I must have it! I must! I must!$B$B$R, I am authorized to make you a grand deal for that ingot. Perhaps you would be interested to know the secrets of the Hand of Ragnaros? Sulfuras, idiot! The legendary hammer of the Firelord.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7007, 'In exchange for one Sulfuron Ingot, I am authorized to grant you one copy of the plans to forge a Sulfuron hammer.$B$BNow an adventurer like yourself is probably thinking to $ghimself:herself;, \"What would I ever need a hammer like that for when what I already own is far superior?\"$B$BWell let me tell you something, $gmister:missy;, there ain\'t nothing more superior than what the Sulfuron hammer can become!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7008, 'You see, all you gotta do is forge the Sulfuron hammer and find yourself the Eye of Sulfuras. Combine the Eye of Sulfuras with the Sulfuron hammer and it will become Sulfuras! Now I\'m not going to bog you down with any technical information as to where or how or when you can get an Eye of Sulfuras. I\'m sure you\'ll figure that stuff out. Just sign this here contract so that our agreement is binding and our business will be done.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7010, '', 'There are costs with war, $c... ones often overlooked.  Those costs are the orphans - children who have lost their parents to the far-too-numerous conflicts that rage across Azeroth.  Those children who have no one else to turn to often come here to live until they are old enough to fend for themselves.$B$BOne week out of the year during the Spring, the Horde takes time to give something back to these innocents.  This time is called Children\'s Week.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7011, '', 'There are costs with war, $c... ones often overlooked.  Those costs are the orphans - children who have lost their parents to the far-too-numerous conflicts that rage across Azeroth.  Those children who have no one else to turn to often come here to live until they are old enough to fend for themselves.$B$BOne week out of the year during the Spring, the Alliance takes time to give something back to these innocents.  This time is called Children\'s Week.$b', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7012, '', 'Why... that is very sweet of you.  I gather that you are volunteering time for Children\'s Week, yes $c?  Well, you are to be commended.  It is imperative that we remember those less fortunate - especially the children.$B$BI\'d be happy to sign an autograph.  Here you go.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7013, '年轻人，欢迎你来到雷霆崖。在这儿你能卸下负担，享受庇佑。我们欢迎所有的部落居民来到雷霆崖。$B$B我的朋友，灵魂都会来这个神圣的地方复活。尊敬他们吧，这也是尊敬你自己。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7014, '啊，毫无疑问，他一定是想要成为一名战士!我想你是来参加儿童周的，是吗?你的行为堪称楷模，其他人必定会以你为榜样的，这令那些疲惫不堪的灵魂倍感欣慰。$B$B我很乐意帮助你和这位年轻的男子。嗯……我要给他提些什么建议呢?听听灵魂怎么说吧，是的……或许是关於吃掉所有蔬菜……', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
