INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7056, '要召唤恐惧战马，你必须首先制造出强力召唤法阵。恐惧战马只可能在法阵中生成。$B$B要制造强力召唤法阵需要三种器具:达斯莫拉之铃铛、黑暗战车之轮和末日蜡烛。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
