INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7094, '是啊，那倒是真的，我瞎得跟蝙蝠一样!而且还醉得...呃，醉得好像喝了很多酒。$B$B其实啊，$R，我跟你说，就是酒精让我感觉更敏锐的。大家都知道，只要你丧失了一种感觉，那么其他的感觉就会变得更加敏锐。这两天啊，我的酒醉感尤其超厉害的!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
