INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7177, '在兽人的新主城奥格瑞玛地底下的，一系列火山洞穴形成了怒焰裂谷，最近，有传言说一个膜拜追随恶魔暗影议会的邪教居住在裂谷的深处，这个邪教——据传叫做火刃教，已经威胁到了杜隆塔尔的国家主权，人们认为兽人领袖萨尔知道火刃教的存在，并且选择了不去摧毁它，希望它的成员能带他找到暗影议会。 $B', '在兽人的新主城奥格瑞玛地底下的，一系列火山洞穴形成了怒焰裂谷，最近，有传言说一个膜拜追随恶魔暗影议会的邪教居住在裂谷的深处，这个邪教——据传叫做火刃教，已经威胁到了杜隆塔尔的国家主权，人们认为兽人领袖萨尔知道火刃教的存在，并且选择了不去摧毁它，希望它的成员能带他找到暗影议会。 $B', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
