INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7182, '这些图纸被守卫着。和札米克谈话来引开守卫的注意!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7183, '最近，一个叫做纳拉雷克斯的暗夜精灵德鲁伊在贫瘠之地的中心发现了一系列地底洞穴，他认为能靠洞穴的泉水来为贫瘠之地恢复生机，德鲁伊从虚幻的翡翠之梦获取能量，后来他的梦境不知怎么的变得十分可怕，很快，哀嚎洞穴称为了邪恶的兽窝、致命的禁地，据说纳拉雷克斯仍然被困于翡翠梦境边缘的迷宫某处。', '最近，一个叫做纳拉雷克斯的暗夜精灵德鲁伊在贫瘠之地的中心发现了一系列地底洞穴，他认为能靠洞穴的泉水来为贫瘠之地恢复生机，德鲁伊从虚幻的翡翠之梦获取能量，后来他的梦境不知怎么的变得十分可怕，很快，哀嚎洞穴称为了邪恶的兽窝、致命的禁地，据说纳拉雷克斯仍然被困于翡翠梦境边缘的迷宫某处。', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7185, '在第三次战役中达拉然的法师们作为平平，被天灾军团战败，大法师阿鲁高决定召唤异度空间的狼族来给己方势力增添实力，狼人屠杀了天灾士兵，但是很快，屠杀的目标就轮到了法师们自己以及最高尊者席瓦莱恩男爵，城堡很快沦陷成为了阴影和废墟。阿鲁高愧疚致狂，竟认狼为子，逃到了附近的“影牙城堡”。$B', '在第三次战役中达拉然的法师们作为平平，被天灾军团战败，大法师阿鲁高决定召唤异度空间的狼族来给己方势力增添实力，狼人屠杀了天灾士兵，但是很快，屠杀的目标就轮到了法师们自己以及最高尊者席瓦莱恩男爵，城堡很快沦陷成为了阴影和废墟。阿鲁高愧疚致狂，竟认狼为子，逃到了附近的“影牙城堡”。$B', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7186, '', 'Aye, you there!  If you want to earn the respect of the Darkspear, then you can start by donating valuable resources for those in need!  Textiles and bandages don\'t grow on trees, ya know.$B$BRather than cash in various cloth pieces you find adventuring, donate them!  Doing so will earn you the recognition of the Darkspear!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7187, '死亡矿井曾经是人类领地上最大的金矿，如今已经变成了被迪菲亚兄弟会占据的秘密领地，他们通过阴暗的秘密隧道进入矿井。传闻，这些强盗找到了聪明的地精，在地底帮助他们建立某种可怕的玩意，但也可能仅仅是传闻。据传进入死亡矿井需要经过宁静安详的的月溪镇。$B', '死亡矿井曾经是人类领地上最大的金矿，如今已经变成了被迪菲亚兄弟会占据的秘密领地，他们通过阴暗的秘密隧道进入矿井。传闻，这些强盗找到了聪明的地精，在地底帮助他们建立某种可怕的玩意，但也可能仅仅是传闻。据传进入死亡矿井需要经过宁静安详的的月溪镇。$B', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7188, '', 'In these trying times, a simple donation of cloth can go a long way to help those less fortunate than ourselves.  Cloth goes a long way to making garments and bandages - both important for the war effort!$B$BIf you are interested in donating cloth pieces to the orcs of Orgrimmar, I will gladly accept them and make sure you receive the proper recognition for doing so.$B$BFor the Horde!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7189, '就是你……在这迫切的时期是你帮助被遗忘者最好的时机。好好听着，我不喜欢重复我说过的话。$B$B我们现在极度缺乏布料。绷带和任何布料储存量都很低，我们希望接受捐赠来补充库存。你的捐赠不会有任何经济补偿─这就是捐赠的含义─你的捐赠一定会为我们所赏识。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7190, '嘿，$G兄弟:姐妹;……我希望可以打扰你一下，如果可以的话。$B$B我们在通过试炼和进行家园重建中经历了很多。我们现在极度缺乏布料。如果你有一些多余的话，我希望你可以捐献给我们。你一定会为此赢得所有牛头人的尊重的!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7191, '怒焰峡谷的入口就在奥格瑞玛中心的暗影裂口。', '怒焰峡谷的入口就在奥格瑞玛中心的暗影裂口。', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7192, '哀嚎洞穴的入口就在贫瘠之地十字路口西南边的甜水绿洲。', '哀嚎洞穴的入口就在贫瘠之地十字路口西南边的甜水绿洲。', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7193, '嘿，朋友─请等一下，如果可以的话……$B$B我们在重建泰达希尔的过程中经历了很多的痛苦。现在，我们不仅要忍受这些痛苦，而且我们还发现我们的布料存储已经很少了。如果你有多余的布料，那我希望你可以捐赠出它们。你一定因为帮助夜精灵赢得我们的尊重!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7194, '诺姆瑞根的流亡者需要大量的布料资源!也许你可以帮帮我们，是不是?$B$B把你多余的布料资源带给我们。通过捐献它们，你可以获得所有侏儒的尊重。这是金钱无法换的!好吧，布料可以帮助我们，但是它们意义绝不限於这些……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7195, '', 'Aye, you there!  If you want to earn the respect of the dwarves of Ironforge, then you can start by donating valuable resources!  Ironforge finds itself running low on cloth piece; textiles and bandages don\'t grow on trees, ya know!$B$BRather than cash in various cloth pieces you find adventuring, donate them!  Doing so will earn you the recognition of the dwarven people of Ironforge!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
