INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7207, '血色修道院曾经是一个学习与启迪的中心。随天灾亡灵在第三次战争出现，和平的修道院被改建成了血色十字军的堡垒。血色十字军不能容忍所有非人类的种族，不管是否联盟或者关系如何。他们认为，任何外族都是潜在的亡灵瘟疫——必须予以销毁。$B', '血色修道院曾经是一个学习与启迪的中心。随天灾亡灵在第三次战争出现，和平的修道院被改建成了血色十字军的堡垒。血色十字军不能容忍所有非人类的种族，不管是否联盟或者关系如何。他们认为，任何外族都是潜在的亡灵瘟疫——必须予以销毁。$B', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
