INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7209, '剃刀高地和剃刀沼泽一样由巨大的藤蔓组成，剃刀高地是野猪人的传统都城。在那错综复杂的荆棘迷宫中居住着大群忠诚的野猪人军队，以及他们的高等祭司——亡首部族。然而最近，一股黑暗的力量笼罩了这座原始的洞穴。天谴军团的爪牙在巫妖寒冰之王亚门纳尔的带领下控制了野猪部族，并将布满荆棘的迷宫变成了亡灵力量的堡垒。现在野猪人正在奋力战斗来夺回他们的城市，并阻止亚门纳尔继续控制贫瘠之地。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
