INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7242, '祖尔金有一天会回来的，$R。记住我的话。在那一天到来之前，我们会守住我们的位置并保护森林食人妖的数量。$B$B作为恶齿村的猎人首领，我的责任就是确保我们居住环境的安全。$B$B我对这个区域周围的野兽都要进行监控。$B$B你是不是来找些活干的?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
