INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7395, '你好。我是克洛诺斯－－过目不忘的克洛诺斯。我的记忆力毫无瑕疵，而且连最微小的细节都记得一清二楚。$B$B相信我。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
