INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (7535, '你好，$C。现在正是收获节－一个感恩与追思的时间。我们举办盛宴感激所有我们应该感激的人，追忆我们的祖先和英雄，感谢他们给我们留下的宝贵遗产。$B$B请尽情享受这盛宴吧。我还提供一些烟火来庆祝这个美好的日子。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
