INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8317, '当你将爱之硬币给人民，你将收到他们不同的礼物作为回礼。有些礼物你会想留着自己用，但其他的就可以收集在一起送给你最喜欢的英雄!拜访丘比特·传播者把礼物交给他。$B$B丘比特就在伯瓦尔，麦格尼和泰兰妲附近。', '当你将爱之硬币给人民，你将收到他们不同的礼物作为回礼。有些礼物你会想留着自己用，但其他的就可以收集在一起送给你最喜欢的英雄!拜访丘比特·传播者把礼物交给他。$B$B丘比特就在伯瓦尔，麦格尼和泰兰妲附近。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8319, '当你将爱之硬币给人民，你将收到他们不同的礼物作为回礼。有些礼物你会想留着自己用，但其他的就可以收集在一起送给你最喜欢的英雄!拜访丘比特·传播者把礼物交给他。$B$B传播者就在凯恩，希瓦娜斯和索尔附近。', '当你将爱之硬币给人民，你将收到他们不同的礼物作为回礼。有些礼物你会想留着自己用，但其他的就可以收集在一起送给你最喜欢的英雄!拜访丘比特·传播者把礼物交给他。$B$B传播者就在凯恩，希瓦娜斯和索尔附近。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8321, '这本秘典的内容就像潮水不断流出。你觉得好像你脑中的思绪被榨乾并被这本书消化掉。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8326, '', 'The assortment of images, shapes, and markings come together before your eyes. The book seems to know your skill with leatherworking and offers you a choice - but be warned - you may only select one and once you have done so, may not change your mind!:', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8327, '', 'I cannot teach you dragonscale leatherworking, $N. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8332, '索尔让我进入他领地中的圣域是对我释出相当大的善意。无论如何，我不求久留。给我们的领导者带来任何麻烦都是极大的侮辱。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8333, '', 'Try not to draw too much attention, I\'m waiting for the King to grant me audience.  Not that I mind waiting, I feel pretty safe around all these burly guards.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8334, '丧命的危险是我常碰到的事。但失去亚希达是我不能容忍的事，就算我死了也是一样。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8335, '你好，朋友!想不想看一些精华电容器?或是精细的全方位鬼灵吸收器?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8336, '什么事？你没看见我很忙吗？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8344, '', 'I\'ll be fine for now, don\'t worry about me.  I\'ll find a way out of here once I\'ve performed some cleansing rituals in this room.  Foul magic taints this place, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8347, '', 'Our newest attraction, and at no cost to you I might add, these miniature tanks, or Tonks as we\'ve cleverly named them, can be controlled from any of the Tonk Control Consoles spread throughout the faire.$B$BAll that you have to do is activate an available control console. Each tonk will come equipped with a stock set of devices, and a random special power to unleash upon your enemy!$B$BHold epic battles with your friends, cruise around, or just cause wild mayhem... the choice is yours!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8349, '', 'The Blastenheimer 5000 Ultra Cannon is a state-of-the-art single person projectile device. Delivering its payload at near the speed of a dragon, the cannon is the pride and joy of the Blastenheimer family, world renown for their aeronautical and combustible feats!$B$BIf you\'d like to be launched through the skies to fly free as a bird, seek out Maxima Blastenheimer on the south edge of Thunder Bluff\'s central rise. She\'ll aim you at the target far to south on Stonebull Lake!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8350, NULL, 'It is said that Kel\'Thuzad seeks the legendary Atiesh, greatstaff of the Guardian.$B$BYes, the very staff once held by Medivh.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8351, '当碰到危害这个世界的事物时，我们圣光兄弟会相较之下是不会手下留情的。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8352, '巫妖王想让自己名扬四海。绝不能让他得逞。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8353, '什么是圣光兄弟会?嗯...我们都是银色黎明的核心成员，不受道德、罪恶感和没用的人类情感所操控。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8354, NULL, 'The Argent Dawn might be impotent idealists but their delusional leader knows that they are outmatched. I have been sent here as an emissary of the Scarlet Crusade. An ambassador, if you will... It is doubtful that we could even dent the armies of Kel\'Thuzad - united or not - but we might be able to contain them long enough for some sort of miracle to happen.$B$BAs for me? I\'ll die in battle...$B$B<Marjhan shrugs.>$B', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8355, '头衔是骗人的。你了解我后，你就知道我是个好人。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8356, '他们都派了我的兄弟，洛克希，到这个集会。他连他的猎犬都顾不好了，更别说有资格代表血色十字军。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8357, '我一直在期...$B$B<林布拉特咳嗽。>$B$B不好意思，老毛病。我是陶土议会的林布拉特。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8358, NULL, 'Hello, $c. Please excuse me as I am having a conversation with Rimblat. He believes that even this land is not beyond saving.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8359, '你要什么，鼠辈?知道为什么大家都叫我施怒者?在我证明之前，你赶快给我滚。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8363, '第一套无畏护甲是专为血色大领主莫格莱尼订做的。完成时，大领主觉得自己活生生像是从冥府来的恶魔。$B$B没错，这的确是个绝佳机会亲见莫格莱尼精神饱满的率领血色十字军，齐声呐喊、高举挥舞着灰烬使者，眼看另一端敌军於燃火飞尘之中在他的双脚下慌张撤离。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8364, '在饱受争议的死亡事件之后，洛丹伦的骑士与战士为了向他们倒下的英雄表示敬意，纷纷都穿起类似的战袍。而莫格莱尼的死对科尔苏加德是伟大的胜利，最后殉难的结果要一直到战争结束才知分晓。$B$B一王倒下，他强必崛起…', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8366, '我就是你口中的资本家，$R。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8374, '喔，哈罗…嗯，你是?$B$B不要紧，是什么风把你吹来我在这美丽乡村的离家之园啊?', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8375, '嗨，$C。想必你们一定玩得很开心吧?你们觉得的派对好玩吗?希望我们派对上的东西都够供应。饮料跟开胃点心如果不够就是派对的致命伤啊，对吧?$B$B嗯，好啦，我要去招待其他宾客罗。尽兴玩。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8378, '', 'Greetings, my $g lord : lady;. I trust that you are having a pleasant time?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8379, '', 'Isn\'t this just so much fun? I\'m so glad that Lord Saltheril throws these parties every night, especially with all of that nastiness occurring to the south. Kind of just makes all of that go away, don\'t you think?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8388, NULL, 'Like Kel\'Thuzad, I too was once a mage of the Kirin Tor. And like Kel\'Thuzad, I seek the legendary Atiesh, greatstaff of the Guardian. Our fates are intertwined as such, $c.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8389, NULL, 'Progeny of Aegwynn, he that would be the last Guardian of Tirisfal... Medivh.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8390, NULL, 'It is a story of legend and one that I do not have the time nor the inclination to retell. Just know that Medivh was the last of the Guardians. The greatstaff of Atiesh was a focal point of his power and a focal point of corruption.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8391, NULL, 'The corruption of a fallen titan. The corruption of the Destroyer. After all, the staff was forged from the seed of hate. That is, of course, what has brought me here...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8392, NULL, 'After the fall of Karazhan, the Kirin Tor dispatched an assembly of mages to search the tower and seize all items of import... for the purposes of \"research.\" It was then that the fabled staff was discovered and brought back to Dalaran.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8393, NULL, 'What followed were a series of tragic events, leading to the death of every Kirin Tor mage that came into contact with Atiesh.$B$BYou see, it was an artifact with sentience. A sentience granted to it by Sargeras...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8394, NULL, '<Angela nods.>$B$BAfter the deaths, the Six decided that the best course of action was to seal the staff behind impenetrable barriers of magic and appoint a watcher: A guardian of the staff... I would be that watcher.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8395, NULL, 'There is not much more to tell. Dalaran was nearly wholly destroyed by Archimonde, the staff lost, presumably shattered by the barrier\'s fail-safe mechanisms.$B$BThere are those that believe the staff to be the cause of Archimonde\'s reprisal.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8396, NULL, 'After the fall of Dalaran, I took my leave from the Kirin Tor. I spent years watching over Atiesh, only to have my purpose amongst the Kirin Tor taken from me in one fell swoop. I would find the staff, though it might take a lifetime. That is how I came to join the Argent Dawn and why I am here now.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8397, NULL, 'I have tracked the various fragments of the staff across this world and in doing so, discovered Kel\'Thuzad\'s plans to reform the staff. He has already collected several of the staff\'s fragments - which are being held by his most trusted lieutenants.$B$BI cannot emphasize enough the importance of keeping the greatstaff of the Guardian out of Kel\'Thuzad\'s grasp. Frankly, I do not know what the result would be, other than a catastrophe of epic proportion.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8398, '这就是权力，低能儿。<麦特斯眼眸加深。>$B$B这些傻瓜派了像你这样的白痴到纳克萨玛斯，取回玷污的护甲以便净化它，重组零件恢复昔日光采。$B$B<麦特斯啐了一口。>就是这种愚蠢至极的想法让我反胃想吐。$B$B我也去寻找护甲，我要找玷污以及被亵渎最深的护甲。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8399, '我不会装模作样，蛆虫。这种黑暗物品所支配的力量可以把我们两个都摧毁。这就是为什么价格如此高昂的原因。如果我就要死了，也会是个富翁。$B$B你死了我也不会对你吐一口豺狼人的口水，照我的话做就对了。把我要的东西带来，我们就能双方受惠 - 或者死;我已经解释过了……', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8401, '您好，旅行者。我奉命在此接待主人的贵客，所有携带着卡拉赞钥匙的人都会被奉为上宾。如果您有什么疑问，请尽管开口。有什么需要我帮忙的吗？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8402, '我们对那些失心者太仁慈了，要我说，我们应该拔出剑刺向他们。$B$B他们在阳帆港的大胆行径是不可饶恕的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8403, '', 'It\'s been a long day of cutting down the mindless Scourge that endlessly parade down the Dead Scar.  I hope you\'re here to lend a hand and not to waste my time with idle chatter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8404, '啊，主人的贵客。希望您在这里没有到处乱走。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8406, '这些该死的歹徒！我能指望你助我一臂之力吗？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8407, '我不想教牛头人骑乘迅猛龙。', '我不想教牛头人骑乘迅猛龙。', '很高兴见到你 $N', '很高兴见到你 $N', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8408, '那些污秽的野兽充斥此地。$B$B如果你喜欢在户外狩猎的话，你可以在病木林跟恐惧谷寻获牠们大部分的踪迹，也请特别留意恶蛛隧道。牠们也可能出没在东瘟疫之地的天谴军团紮营处。$B$B假如你热衷於地城，你在斯坦索姆会发现更多牠们的踪影，另外，在通灵学院可以找到一些通灵魔。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8409, '你是笨蛋吗?遗失了你回程时那堆散乱路旁的骷髅类生物?<洛汉叹气。>$B$B如果你是户外型的人，你可在东、西瘟疫之地找到骷髅生物。相信我，你不能搞丢牠们－如果他们可以的话，他们一定不会放过你。倘若你是地城冒险型，斯坦索姆和通灵学院正是寻找为骨头碎片的地方。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8410, '', 'Another lovely, gloomy day here in Tranquillien, wouldn\'t you say, $c?$B$BWhat can I do for you?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8411, '没有什么是比翱翔天空更自由的事，$C。有什么需要我帮忙的吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8412, '哈罗，朋友。需要什么吗?', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8413, '', 'Heading down to Tranquillien today, $c, or is there something else that I can do for you?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8414, NULL, 'Ah, a good question.$B$BIf you prefer hunting in the great outdoors, there are elementals here in the Plaguelands, Azshara, Burning Steppes, Felwood, Silithus, Winterspring, and even Un\'Goro Crater.$B$BIf you are the type that laughs in the face of danger, then perhaps you should visit Dire Maul. I have heard that a great many elemental abberations exist within its walls. You would also do well in seeking out the elementals employed by the Dark Iron dwarves of Blackrock Depths.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8415, '这是很严肃的问题?你真的不知道?$B$B如果你是懦弱型的深怕脏了手，你可以在燃烧平原与灼热峡谷，找找生物身上的黑铁碎块。$B$B如果你是嗜血如命的冒险型，你到黑石塔和黑石深渊的地城，会发现有数以万计的黑铁碎块等着你。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8416, NULL, 'If you are one that prefers to hunt in the outdoor world, both Felwood\'s treants and Un\'Goro\'s lashers are an excellent source of fronds.$B$BIf you are more of a risk-taker, the eastern and western wings of Dire Maul will provide you with a nearly limitless supply of savage fronds.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8417, '$C，看看他们对我们可爱的土地做了什么。天谴军团一定要被消灭!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8418, '', 'It\'s going to take a bit of work, but I think we can save this blood elf\'s life.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8419, '我…不行了…我失败了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8421, '', 'It is shameful that we sin\'dorei have lost so much of what we used to call home.  It shall not be long before we regain our strength and retake our southern lands back from the Scourge!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8422, '我没那时间跟你玩猜谜游戏，$R。说出你的来意，然后消失在我眼前吧!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8423, '高阶执行官和阿莉法娜奥丝夫人让我在哪边枯等，但我真的没时间!我朋友正在垂死边缘挣扎啊，$C!', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8424, '', 'I sent Ranger Salissa south to the enclave and Ranger Lethvalin west to Tranquillien more than a day ago! Have you seen either of them? Are you the aid that\'s been sent back?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8425, '有时候我觉得我好像永远也无法回归到简单正常的生活来效忠阿莉法娜奥丝夫人。我永远也离不开战争。$B$B幸好总是有像你这样的人热切於见到奎尔萨拉斯回到它原来的样貌。摧毁天谴军团，$C!不管你在何方都要摧毁他们!', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8426, '', 'Ah, here\'s the $g hero : heroine; of the hour now! Good to see you again, $N! I trust that you\'re still destroying the Scourge wherever you find them?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8427, NULL, 'I\'m sure that help from Farstrider Enclave will be here soon to help me and this useless lieutenant get to safety. You might not want to stick around; I\'ve heard all sorts of strange sounds coming from the lake and that village over yonder.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8428, '我得到消息说你帮助了游侠瓦兰娜和派往塞布索雷的上尉。$B$B感谢你，$C。我想我现在应该回远行者营地了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8429, '是什么风把你吹到这里来，$C?', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8431, '啊，你好，$R。如你所见，我还没开始营业。所以，如果你不介意，我要先忙了。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8432, '很高兴再见到你，$C。不，真的。你是我会在这里赚钱，掌管一切的原因。$B$B那么，你今天想跟我买些什么?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8433, '', 'Welcome to Tranquillien, $c. I apologize for the state that you find it in. As you can see, we\'ve been having some trouble, but the Forsaken, and their Lady Sylvanas Windrunner, have been gracious enough to lend us a great deal of assistance in our time of need.$B$BIf you can lend a hand please do not hesitate to do so. We can use all of the help that we can get in our struggle against the myriad forces arrayed against us.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8434, '该是部落和联盟注意北裂境和巫妖王入侵的时候了。最近这些日子，整个卡林多和东部王国都遭到攻击。你是否会开始全副武装保护你的土地免於他们的摧毁呢?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8435, '剑而已，不用害怕。要小心羊肉才是真的！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8438, '恐怖的事情是，发生在那些骡身上的事…这次失败，梅兹一定需要我的掩护。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8439, '', 'It is very important that we find out what the the night elves are up to, $c! Please concentrate all of your efforts toward that end.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8440, '', 'We are most pleased with your actions against the night elves. When you uncovered their plot to use the ley-line nexi and their so-called moon crystals to spy upon us, you strengthened our position here by an order of magnitude.$B$BYou have our gratitude, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8441, '你是援军吗?夜精灵已被击溃了!', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8442, '你将安欧恩的月水晶能源关闭是一件好事，$C!如果你想为我这里的研究提供一点贡献欢迎你随时过来。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8445, '在这样的情况之下我要怎么工作?!还有全部这些恶心的被遗忘者!我为安宁地和奎尔萨拉斯悲叹!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8446, '嗯……你想要什么，$R？你没看到我这里正忙着吗？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8447, '$R，报告!你带了什么重要的消息来?快点说吧……我和阿莉法娜奥丝夫人正在做策略的商讨。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8448, NULL, 'Welcome to Light\'s Hope Chapel, stranger.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8452, '我们的商队大约一天出队2次，万一天谴军团又去阻止迪笛儿，还有要送往前线的那些骡，那到时我就会被指责。这还是我前所未见最疯狂的事情。但愿我们有英勇的冒险精神能保护这运送过程...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8454, '我事情太多，时间根本不够用，$R!拜托，别挡我路。', '我事情太多，时间根本不够用，$R!拜托，别挡我路。', '很高兴见到你 $N', '很高兴见到你 $N', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8455, '我事情太多，时间根本不够用，$R!拜托，别挡我路。$B$B万一你被蛆虫粘液跟腐烂食屍鬼感染的话，我有能力治疗各种疾病。', '我事情太多，时间根本不够用，$R!拜托，别挡我路。$B$B万一你被蛆虫粘液跟腐烂食屍鬼感染的话，我有能力治疗各种疾病。', '很高兴见到你 $N', '很高兴见到你 $N', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8456, '<当你将恢复药剂倒入他嘴里后血精灵很快恢复意识。>$B$B我欠你一命，$C。没有你的帮忙，我就得屈服於天谴军团的痛苦折磨了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8457, '<血精灵倒在地上看起来变的很迟缓。>', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8458, '你好，$R。我是辛多雷——也就是血精灵一族的大使欧诺·炎伤。我忠心希望我们与部落新建立起来的友好关系对双方都有好处。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8459, '', '<The Forsaken female is curled up on the floor, her face in a grimace.  She does not appear capable of getting up on her own at the moment.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8460, '', 'Ahhhhggg... this pain in my head is killing me.$B$BThey must\'ve sent you to rescue me.  Took them long enough!  Do you have any idea of the things they do to prisoners here?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8461, '<血精灵毫无生气地躺在桌上。虽然他还有呼吸，但他明显地中了高效的镇静毒素。>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8462, '你好，$R。我是听从高阶执行官的建议来到这里。我有信心只要我们团结一致就能打造一个前所未有的强大部落并消灭所有的天谴军团威胁。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8463, '<当恢复药剂开始生效瓦尼司激烈地咳嗽。>$B$B谢谢你救了我。我一定要立刻回到安宁地!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8464, '从永歌森林三不五时吹来的风真让我作呕，你懂我的意思吧。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8466, '', 'If it is a matter of import that you wish to speak on, $c, it may be more appropriate to seek an audience with Dame Auriferous. I warn you though, she is very busy and you may be in for quite a wait.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8467, '', 'Normally it would not be my place to speak with you directly; rather, you would seek an audience with milady. However, I did give you a task directly, so what is it that we need to discuss?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8468, '', '$C, Dame Auriferous has conveyed to me her great pleasure in the service that you have done us in the past. She wishes to extend to you her hope that you will continue to be a champion of sin\'dorei interests here in the Ghostlands.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8469, '这块告示写着许多宣布事项和奖励办法。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8470, '', 'We\'ve important work to do, $N.  Let us attack our enemies while their guard is down.  Let us show them that sin\'dorei vengeance never forgets nor forgives.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8471, '战争浪潮又再度袭来。巫妖王的亡域军队已经从寒冷的北方，包围了我们的土地。他派出了飞行的亡域入侵全世界。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8472, '', 'Well this is troublesome! How am I supposed to collect the ore samples for the magisters if the mines have been overrun with gnoll vermin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8474, '她会在哪?我满早以前就派我的学徒，莎夏莉雅去西边的光底矿坑了。我们需要把那些矿石样本带回来，要快，否则我们的实验就会失败!', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8475, '如果没有那个光底矿石，这样的情况下，我们的研究一定会失败。$B$B等莎夏莉雅回来这里再说吧。我有一些有趣的任务要交给她，你可以相信我!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8476, '', 'Sin\'dorei have lost far too much in the past years.  Our land, our homes, even our identities...$B$BIt\'s about time we started taking it all back.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8477, '在我们战胜像天谴军团和阿曼尼这些外患之前，一定要先克服内忧。鄙恶者已经惹人厌够久了，是时候消灭他们了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8478, '', 'Think you could help me to get out of here?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8479, '在那几张羊皮纸里有核对清单和其他远行者的资料，其中有一张特别抢眼。那是一张丑陋的食人妖素描，可能是你见过最可怕的生物!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8480, '的确，冬泉谷山丘一带正尝试要抵挡重生的天谴军团攻击。你的帮忙对他们来说很重要。$B$B剩余的亡域:$2284W', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8481, '这地方目前暂时脱离天谴军团的威胁了。我担心他们很快又要回来了。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8484, '如果援军不能及时赶到燃烧平原，我怕天谴军团会在那里建立指挥基地。如果你可以，就应该赶快到那边协助防卫部队。$B$B剩余的亡域:$2281W', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8485, '', 'Many an advantage can be gained from getting to know your enemy.  Take that lesson to heart, $N.$B$BThe more we learn about the Scourge the better prepared we are to defeat them.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8486, '天谴军团正在攻击地区设置火力点，接收横跨天际的亡域的通讯协助，和其他方面的军援，我们知道，将他们根除的方法就是杀掉所有地面基地的军力。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8487, '', 'You\'re welcome to stay at the Farstrider Enclave for as long as you\'d like, $c.  We welcome any who would aid us in our endeavors in the Ghostlands.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8488, '流散的秘法力量会对大地，元素，甚至亡者的灵魂做出奇怪的事情。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8489, '高阶执行官是个很忙的人，$C。有什么事我可以帮你吗?', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8490, '你是我们的楷模，$N。在对付光底矿坑的豺狼人和阿曼尼地下墓穴的屍化食人妖之后，你对我们的敌人击出了致命的一击!$B$B$G做得好!:我们应该找个时间好好喝一杯，你觉得怎么样?;', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8491, '很高兴再见到你，$G老兄!:美女!;我们合作愉快;$G下回再见。:我希望我们还有机会合作。;', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8492, '人们对你将项链归还给希尔瓦娜斯女王一事议论纷纷。我坚持认为你做了件好事，不要担心女王会对此做何反应。$B$B去做你自己的事好了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8493, '我能说什么，$C?你是个真正的$G英雄:女英雄;!你不仅个人救了我许多的游侠，还帮助我们对影松食人妖击出致命一击。你确定你不要留下来成为我手下的远行者之一吗?$B$B再见了，$N。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8495, '你对我们对抗影松食人妖的帮助可说是无法比拟，$C。继续保持我们或许就能将注意力转回来对付天谴军团。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8496, '你给了影松食人妖重重一击，$N。我只希望我们可以在他们从祖阿曼获得援军前召集我的军队铲除他们。$B$B如果安宁地愿意给我们支援会非常好。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8498, '', 'Certainly, $N. You only need to tell me what reputation you would like to know about.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8499, '', 'Certainly, $N.$B$BAdventurers that the Dawn views as honored must submit a combined total of 40 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 150 insignias.$B$BI must admit, the price is high even for those honored amongst the Dawn.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8500, '', 'Certainly, $N.$B$BAdventurers that the Dawn views as revered must submit a combined total of 14 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 90 insignias.$B$BThose that are revered by the Dawn will find the cost of superior items quite reasonable! The cost for the more powerful items is still high.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8501, '情况比许多人所想的还要严重。被遗忘者是我们在这里的盟友，帮助我们对抗共同的敌人，天谴军团。$B$B当我这样说的时候请相信我，没有他们的话就不会有鬼魂之地，而且天谴军团也会早已进入永歌森林!$B$B但更重要的是，我们需要你的帮助来重拾我们曾经骄傲的土地!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8502, '', 'Certainly, $N.$B$BAdventurers that the Dawn views as exalted must submit a combined total of 12 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 54 insignias.$B$BThose exalted in the eyes of the Dawn receive the greatest discounts.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8503, '', 'Certainly, $N.$B$BAdventurers that the Dawn views as friendly must submit a combined total of 60 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 220 insignias.$B$BI must admit, the price is quite high for adventurers not favored by the Dawn.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8504, '这些符文石保护永歌森林的大地不受天谴军团污染。西边的符文石很久前就失去作用了，逼迫我们燃烧它周围的土壤。$B$B只要我还活着我就绝不允许剩下的符文石再次失效。我发誓。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8505, '', 'Well, you didn\'t come all this way out here to jaw over a cup of tea. Let\'s see how you might be able to help get us out of this situation.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8507, '', 'Is this the best they could do?$B$B<Omarion musters the strength to laugh.>$B$BYou do not possess the resolve to face a monster like Kel\'Thuzad. You will crumble before his minions - just like those that have come before you...$B$BLeave me here to die. I refuse to return to a world that champions the likes of you.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8513, '在某个夜里工匠大师欧玛利安突然消失了。就像风中的号角声...咻!消失了!呸，现在只剩下我一个人帮前线的战士们铸造东西了。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8515, '$N，辛多雷和被遗忘者在幽魂之地的目标是一致的。我们将共同击败亡灵天灾，或许在我们进军外域的过程中，被遗忘者也将成为最有价值的盟友……谁知道呢。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8516, '', '<Omarion grumbles something under his breath.>$B$BBut of course I can help you, dear $g boy:girl;. It is my dying wish... my final desire. Please, take this handbook. The information on its pages are a compilation of all that I know and have learned in the past 30 some-odd years of my miserable life. Give the handbook to that good for nothing apprentice of mine that\'s probably sitting on his fat duff at Light\'s Hope Chapel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8517, '<塔希斯呻吟。>$B$B或许还有时间……仔细听好，凡人。$B$B我在时间与空间里搜索。和一个极度邪恶的恶魔主人打过交道……$B$B<塔希斯在手中咳出血来。>$B$B你会……你会相信就连伊露恩也不理我了吗，$R?死在这里，现在这样的情势之下……', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8518, '我做过的事……这样恐怖的事……$B$B<塔希斯开始啜泣。>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8519, '我是个精灵贵族，$R。现在已经没有意义了。虽然，曾有一段时间，这个世界是被精灵贵族所统治的。一万年过去了……', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8520, '我会把它拿回来的，凡人。阿泰丝……我会用它来使精灵贵族获得新生。我会让他们全都复原。就连我们的女王也是……', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8521, '<塔希斯虚弱地抓住你的衣领。即使濒临死亡你也在他的眼中看到难以想像的力量。>$B$B我是吗?那么仔细听好，因为有更多的疯言疯语要从我嘴里说出来了。在堡垒里面你会发现我毕生的杰作:每一块碎裂的法杖碎片的所在位置。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8522, '阿泰丝，守护者之杖。那个法杖一直以来都在每个提瑞斯法的守护者间流传着。法杖后来被萨格拉斯腐败。最后一名守护者的法杖。麦迪文的法杖。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8523, '<塔希斯的眼神发亮。>$B$B我曾经拥有它……我不顾肯瑞托的警告拥有它。我曾经拥有它，凡人……无尽的力量。做我想做的事的力量，随心所慾。就在那个时候，它从我手中被夺走了。但是我尝过那种滋味……而那滋味值得你付出那样的代价。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8524, '<塔希斯伤的很重。>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8526, '战役进行良好。天灾军团的实力已经被大大削弱了。我相信我们完全能够把他们都赶进海里，这只是时间问题了。我会尽可能地帮助你进行战斗。', '战役进行良好。天灾军团的实力已经被大大削弱了。我相信我们完全能够把他们都赶进海里，这只是时间问题了。我会尽可能地帮助你进行战斗。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8527, '我们对抗天谴军团的第一步进行的很好。我们有了一些成就，希望我们的协助能帮你们获得未来的胜利。', '我们对抗天谴军团的第一步进行的很好。我们有了一些成就，希望我们的协助能帮你们获得未来的胜利。', '很高兴见到你 $N', '很高兴见到你 $N', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8529, '它是在紫罗兰堡垒陷落之后不见的……碎成了四十二个碎片。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8530, '科尔苏加德付出了相当大的努力在尝试重组法杖。这些碎片又再次散落在世界各地，从卡林多到南海都有。我曾经一次拥有二十个碎片 - 直到科尔苏加德的爪牙从我手中将它们偷走。这就是为什么我会在这里的原因。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8531, '你没在听吗?不，我很怀疑如果科尔苏加德组合了法杖我们还能有这段谈话。科尔苏加德握有那个杖头。他的手下拥有四十个阿泰丝之杖的碎片。最后一块 - 杖柄 - 被布莱恩·铜须发现。他最后一次被人看到是在希利苏斯的沙漠;毫无疑问地，离科尔苏加德派遣到这世界的残酷追寻者越远越好。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8534, '横跨这片土地上有肉眼不能见到的能量界线洪流，我们的圣所就是从那里虹吸秘法能量。$B$B在太阳之井消逝之后，它们提供了我们稳定可靠的能量来源。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8535, '晴风村仍然存在，这是对我们种族坚韧不屈的精神的证明。我们绝不让步，直到永远地击败天灾军团。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8536, '肯威，你这下流的家伙!里维加兹让我免於绞刑架的惩罚但我的刑罚也好不到哪里去。', '肯威，你这下流的家伙!里维加兹让我免於绞刑架的惩罚但我的刑罚也好不到哪里去。', '很高兴见到你 $N', '很高兴见到你 $N', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8537, NULL, 'You\'d better not be causing trouble, $N!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8538, '我能为你做些什么，$N?快点说，我很忙!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8539, '我能为你做些什么?不，等等!你能为我做些什么?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8541, '你对我们的事业付出了相当大的忠诚，$N。请让我带你瞧瞧我们永无止尽的元素精华来源。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8542, '欢迎回来，$N。你是个对付拉格纳罗斯的忠诚盟友，我们永远欢迎你。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8548, '达克汗认为我们会还没开战就被天谴军团给击败。我们一定要让他知道他是错的，$N。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8549, '你没看到我正在和这位小姐说话吗?喔，非常好，反正她看起来有点忙。$B$B我只卖最好的酒，国外和国内的葡萄酒都有。要看一看吗?', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8550, '那是萨瑟里避难所的清单吗?没问题，他的身分地位可以获得一瓶最好的酒。当然，任何萨瑟里领主的朋友都可以特殊的价格买到这样上好的葡萄酒。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8551, '我们赢了天谴军团$2219W场战役。做好准备，$N，这场战争要结束还差的远了。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8552, '', 'If you have the coin, I have the food and drink to keep that appetite of yours satisfied.  Every copper goes to keeping the Retreat in tip-top shape.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8553, '', 'Is that a list from Lord Saltheril that you have there? Yes, I can sell you a batch of appetizers, fresh off the fire.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8556, '我出售各式各样的物品，$C。随意看看。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8557, '一个清单……好奇怪。啊对了，萨瑟里领主的烟火。他还没付帐;你是代表他来这里的吗，$C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8558, '', 'Is there no end to these undead?  It seems that for every ghoul or skeleton we kill, two take its place.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8561, '$C，你不会刚好在来这里的路上看到一个信差吧?我们在等待银月城的消息而他们的信差迟到了!', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8564, '有时候我站在这里，望着湖后面的森林，然后我就会看到大地又变的……绿意盎然充满生机。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8565, '我们一定得想出个完美的办法来对付那些臭燻燻的影松食人妖!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8566, '投毒成功，那些巨魔要倒大霉了，$C。你真是个有前途的$G小伙子:姑娘;！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8569, '', 'We must maintain a state of vigilance, $c! Between Dar\'Khan the Betrayer and his Scourge, and the Shadowpine trolls backed by the might of Zul\'Aman, we\'re barely keeping our heads above water here. We really shouldn\'t have to be dividing our attentions!$B$BI hope you\'re here to help.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8570, '', 'I\'m glad that you\'re here to help, $c. The faster we can deal with these trolls, the faster we\'ll get to send the scourge back to where they came from.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8571, '', 'One set of trolls dealt with, one yet to go. You have no idea how much I appreciate what you\'re doing, $N! Before you came along I didn\'t think we\'d ever get the Shadowpine problem under control.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8572, '', 'You wouldn\'t believe some of the rabble that we\'ve had come through here lately. Most are more trouble than the supposed help that they provide. Nothing at all like you.$B$BYou sure you don\'t want to sign up with the Farstriders?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8573, '巫妖王只派了一小批军力来应付艾泽拉斯的每个主城，他们的主力部队周期性地攻击以下的土地:$B$B艾萨拉，诅咒之地，燃烧平原，塔纳利斯沙漠，东瘟疫之地和冬泉谷。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8575, '', 'What brings you here, $N?  We don\'t see too many people around these parts.  With good reason, I suppose.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8576, '', 'Do you wish to learn the blacksmithing profession or is there something else you wish to tell me?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8579, '', 'We are very fortunate to be alive, you and I!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
