INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (8620, '不是我的错……那不是我的错!!!$B$B如果他们照着我的话做一切都不会有事。但是不!他们根本不听。$B$B现在他们都死了。$B$B我只想回家。回到银月城的家。我累垮了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
