INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9021, '', 'Greetings, $N.  As you rise in rank among the forces of Honor Hold, I will give you access to some of the different goods we keep in stock.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9022, '我是埃德门图斯，秘血岗哨的首领。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9023, '欢迎来到萨尔玛，$N。随着你在萨尔玛的声望不断提升，我可以让你使用我们的更多物资。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9024, '需要帮忙照顾你的动物夥伴吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9025, '在阿古斯，麦克艾瑞是我们最神圣的城市。$B$B如果我告诉你走道两旁都排列着珍贵的矿物你会相信吗?河里即使是全黑的也闪着光芒?$B$B我渴望那些日子...已经过了多久了?一千年?一万年?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9029, '欢迎来到秘血岗哨，$C。在我的旅途中，我收集到了各种文明的工具、文物和知识。$B$B如果你需要这些知识，只管来问我就是了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9033, '如果你在血谜岛的时间里只学会一件事，$C，我希望那是警惕。这座岛会先夺去不警惕的生命，再开始削弱最谨慎的战士。$B$B你所到之处，都有一个敌人准备好要夺走你的性命。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9034, '', 'Everything that has happened since the crash is little more than a blur. It seems like a lifetime ago that I was aboard the Exodar.$B$BNow, my reality is restricted to this island with its mutant creatures and endless sea of blood elf agents. Soon, it will be as if I\'ve never known anything different...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9036, '其他人看着这片风景而他们只看到了死亡，腐败，和厄运。没有任何东西短缺，的确，但眼前的景象让人忽略了我们环境所带来的机会。$B$B我不会坐视不管让这些机会被白白忽略，$C。有了你的帮助，我们可以让血色守望这里的其他人看看如何利用环境和其中的生物。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9037, '哈罗，$C。$B$B长者凯索在那里，他是个聪明的人。如果我是你，我会非常小心的和他交易。$B$B他提供我照料伊莱克的工作叫我当一个蠢蛋，我以为是供我吃，训练，等等之类的。后来当他递给我铁锹时一切就很明显了...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9038, '谢谢你的警告，$N!我们会做好准备!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9039, '允诺者，我们感谢你的警告。静松部族会做好准备。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9040, '谢谢你的警告，$N。我们会做好防御。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9041, '我是凯索，伊莱克的统治者，阿古斯之手的追随者。你是新成员吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9042, '我是阿彻鲁斯，秘血岗哨远征军首席指挥官。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9043, '我是往来于这里和秘蓝岛之间的邮差，$C。如果你有什么消息需要递送，很可能是要交给我来办的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9044, '成为阿古斯之手的意义...我们是圣光的密探。我们毫无疑问的效命。没有怀疑的死去。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9045, '是他们派你来找我的吗?我不会丢下我的人回去的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9050, '是的，我是「破碎者」的一员。我相信这不会让你困扰吧?破碎者或德莱尼，我们一定要容忍社会对我们的奚落和嘲笑。和这世界的元素有更好的了解和沟通才是最重要的事。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9051, '请坐，请随意。', '请坐，请随意。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9052, '你好，$C。你需要一只可靠的角鹰兽吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9053, '如果你需要去埃索达，就乘坐我的角鹰兽吧！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9054, '嘘!安静;我正在观察鱼人。$B$B住在一个超大型的乌龟壳里是我愿意为科学所做的牺牲。毕竟，如果鱼人看到我在这里，他们的反应就会因为我的存在而改变，那样会降低我资料的真实度!$B$B身为探险者协会的第一名侏儒成员，我大胆地进入没有矮人敢去的地方!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9056, '你疯了吗?看看你的四周，凡人。你没看到你站在分裂的大地上吗?离开这个地方，以免你遭受和我与人民一样的苦……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9057, '你是个疯了还是瞎了?$B$B我是个鬼魂 - 一个残忍的提醒者，提醒着很久不存在的文明 - 系住一块我无法保护的土地。$B$B很久以前 - 一万年前 - 我是有血有肉的人，就像你一样。我是这块土地的王子也是个龙骑士，受翡翠梦境的伊瑟拉所庇护。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9058, '你没听过伊瑟拉?大自然的守护者?梦境之龙?她是所有绿龙军团的母亲!是的，是伊瑟拉亲自将她的后裔赠送给我的王国。我们和这些高贵的生物肩并肩作战，他们也允许我们骑着他们进入战场对抗我们的敌人。一切都相当地美好，持续了许多个世纪……直到……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9059, '死亡之翼的后代……伊瑟拉的仁慈唤起了死亡之翼的愤怒 - 黑龙军团的庇护者。他们在我们沉睡中袭击我们!许多人在一开始的攻击中就死了，但绿龙军团便来保护我们。$B$B<托瑞斯王子指着这座岛。>$B$B那些曾经伟大的龙群只剩下这些白骨。没有一个幸免。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9060, '我是最后一个死的人。当我感觉我的灵魂离开了我的凡人躯壳，我便发下血誓。$B$B<托瑞斯王子摇摇他的头。>$B$B我和这块土地签下一个契约:我的鲜血为这世界泼洒。我成了唯一守护我人民历史的人。$B$B在我确信罗萨伦龙骑士的故事尚未消失在时间的洪流之前我不会休息的。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9061, '学生就像绵羊。当你引导并密切注意他们，他们就会变的很有用甚至很可爱。$B$B你离开他们一下，他们就从悬崖上跌下或溺死在很浅的河里。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9062, '这个公告牌上张贴着多种公告和执行危险任务的奖励。现在没什么可以吸引你的东西，也许你应该过段时间再回来看看。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9063, '你好，居民。如果你没什么事要问我，那就请你走吧。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9064, '', 'Marshal Windsor has been expecting you, $N. Unfortunately, he is currently indisposed. He should be returning shortly.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9065, '你办到了!太杰出了!温德索尔元帅一直在等你。要不要我现在去通知他?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9066, '嗨，勇士。你的事蹟将会被世代传诵。或许有一天，你的雕像会矗立在暴风城的英雄之间。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9067, '离我远一点，$R!当病痛再次对我缠身就不要再和我接近了。$B$B在它再发生之前不要再来找我。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9068, 'How did you learn about the ore? Not even the Baron knew...$B$BTake the cursed things. They\'ve brought me nothing but misfortune anyway. I hid them in the stables just off the courtyard.$B$B<A low growl begins to emanate from the back of Landen\'s throat.>', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9070, '检查屍体你推论这一定是血骑士晨星，死亡之域失败突击的领导者。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9071, '我可以帮你建立一个公会。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9072, '翻遍血骑士的尸体之后，你找到了那枚徽章。它并没有因为战争和周围的环境而受到任何损伤。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9073, '', 'Elune\'s grace upon you.  How can I serve?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9074, '', 'Greetings, $c.  It is good you\'ve decided to visit us.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9076, '你以荣誉为三人之一效命，$N。我们的时间已到尽头。库罗斯准备好后会告诉你。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9077, '你好，$C。我是库罗斯，三头政治的第二人。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9079, '我看着他们无情的鞭打他。他们残酷的副官一举一动就好像残暴是正常的事一样。经过野蛮的鞭打之后，我可以感觉我已经崩溃了。$B$B看着复仇者萨鲁安忍受他们的殴打，我开始哭泣。并不是因为害怕...而是因为悲伤。看到三头政治的德莱尼被这样对待。要是我能打破我的束缚就好了。要是可以的话...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9080, '在看起来像实体的东西出现后，复仇者就失去意识了。这实在太折磨了，即使是对他而言也一样。残忍的 - 马帝斯，据悉他是被这样称呼的 - 还将传染的水泼洒在萨鲁安毁坏的身上想叫醒他。但萨鲁安并没有醒来。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9081, '我想尖叫可是只有一声低喘迸出我嘴边。我很快就被这次的反叛行动给打败。当我躺在地上，我可以看见马帝斯在萨鲁安的屍体前跺步。他看起来很担心。他声称如果萨鲁安被後打死去，赛罗娜斯或赛罗娜或类似这样的实体会把他的肉体从骨头上剥下。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9082, '赛罗娜斯实体对萨鲁安有个计画...$B$B<书写突然中断，一条长长的线画过页面。这个德莱尼在制造实体时死去。>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9084, '你好，$C。如果你想学习铁匠的艺术，那么你来对地方了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9085, NULL, 'Oh, pay no mind to Buddy.  He won\'t hurt you, he just likes to scratch now and then.$B$BHe\'s just a big, loveable kitty.$B$BAren\'t you Buddy?  Yes, you are, you cute, little thing.  You\'re mommy\'s little baby, that\'s what you are!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9086, '我们塞纳里奥的德鲁伊是自然平衡的保卫者。重点是平衡。当我们被徵召时一定要尽速采取果断的行动。不要让任何人左右你，尤其是我们之中那些只想静观其变的人。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9090, '你是谁?不要来烦我。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9097, '到北方开始你的搜索吧，$N。我们的斥候已锁定海案线旁的星移活塞。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9098, '我的故事?你这样问真好。非常好。$B$B很久以前在德拉诺行星上 - 也就是现在残余的外域 - 所有的德莱尼都和平共处。但是后来燃烧军团来了，他们用邪恶魔法腐败了兽人。$B$B别无选择，我们为生存而作战，但暴露在这些邪恶能量下已对我们一些人产生了可怕的作用，长时间后将我们转变成像我一样的破碎者，或是更糟，看似无脑的失落者。$B$B我要继续吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9099, '我们身体的改变对破碎者和失落者造成副作用，切断我们和圣光的关系。当时我不知道为什么，但身为一个牧师的我坚持要找出原因。我决定进行朝圣来找出答案。$B$B战争结束之后，我前往破碎行星的沙漠恳求圣光回到我们身边。但几十年过去，圣光从未回覆过我的祈求。$B$B你想再听下去吗?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9100, '我很幸运发现一个绿洲并决定留在那里直到我得到一个解答。然后我就坐在那里，只有必要的时候才会移动，以接近水边的生物维生。我引以为傲。$B$B如我所说，几十年过去了都没有一个答案。但我记得有一天傍晚的夕阳特别美丽。一阵微风轻拂大地，我确信我听到了一个声音，但附近并没有人。$B$B你可以想像，我以为我真的疯了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9101, '这风有点不对劲;它停不下来。几周以来它一直吹着大地，不断变大又变小，但就是不停止。而且声音不断呼啸，不过我不太懂那是什么意思，也不知道它究竟是从哪里来的。$B$B我隐居的日子让我学会如何平静下来并检视内心。这样做我才能保持清醒。而就是那时候我才开始了解那声音的意思。它是风发出的声音，有很多话想说!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9102, '它告诉了我秘密和力量，同样也告诉我了真相。圣光并没有遗弃破碎者！很久以前，兽人用来对付德莱尼人的魔能已经侵入了我们的躯体，使我们聆听圣光的能力被封闭了，同时也导致了我们身体的退化。$B$B但是我们的灵魂并没有被封闭，空气给了我另一种选择，让我可以指引我的人民。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9103, '它教了我萨满之道，在某种程度上它也是圣光的另一层面。加入风的是海洋的水，我的营地的火，和我坐着的土。所有元素都有话要说。$B$B过去几年他们教我如何召唤并与世界的元素同在。而且他们告诉我这世界天空中的所有星星，就连这一颗也一样，都是由这些元素形成的。$B$B但终於有一天我觉得我已学得够多足以离开了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9104, '我回到我的人民那里，他们用微笑的脸庞和敞开的双臂迎接我，大部份都是破碎者。我不在的时候德莱尼对我们的偏见仍持续存在。但他们好像早就知道我要来了，而我很快就了解为什么。$B$B费伦，德莱尼的领导者和预言家都在他们的幻象中看过我回来，他们也在那里欢迎我。他要求与我私下谈话而当我诉说我的故事给他听时，他点头并微笑，因为这些他都预见过了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9105, '费伦鼓励我肩起破碎者领导者的职务。但更重要的是，他要我教所有人萨满之道;破碎者和德莱尼都一样。我接受了。$B$B我们后来便知道对某些人有偏见顶多也只是不随和的主张罢了。有些人鄙视破碎者，因为我们总是提醒着令人恐惧的过去。还有其他人认为萨满之道是对圣光的亵渎。$B$B无论如何，这是我的故事。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9106, '如果敌人能知道我们只想和他们和平共处，或许我们就不必冒生命危险攻打他们。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9107, '他们的日子就要来临了，$N。$B$B当那天来临时，我会在那里。', '无论萨尔是否存在，破碎之手氏族的秩序都将延续下去。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
