INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9113, '费斯伍尔喜欢小孢子人。他们拯救费斯伍尔。费斯伍尔不吃他们。$B$B费斯伍尔痛恨纳迦。费斯伍尔猛击!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
