INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9734, '离开我的视线，孩子!你看不见我正在训练吗?就是我，我将是下一名战槌勇士。$B$B呀，我就快要准备就绪然后就能前往旧狞笑骷髅废墟的北面的血之竞技场了。但是想都不要想，孩子。你太弱了。葛尔萨克，竞技场广播员，甚至不会让你偷看一下竞技场，更不用说在它里面的战斗。$B$B现在，在它击败你之前，先击败它。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9735, '好，看看你。你不是高大和强壮的……或许你认为你强壮到足以走进血色竞技场，并且面对战槌勇士吗?$B$B<竞技场斗士笑了。>$B$B不是很可能，不过如果你想要试试你力量，血色竞技场的位置是远北方，面对着分隔纳葛兰和赞格沼泽的山脉。葛尔萨克在旧狞笑骷髅废墟经营表演，而他负责发放奖品。谈到奖品，我听到这季的奖品令人难以置信!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9738, '审问者试图查明这些血精灵正在纳葛兰这里做些什么。从我听到的，他们在追踪来自欧夏刚，西方的钻石山，的水晶粉末残渣。无疑地要用在一些阴险的计画或者混合物里的……$B$B假如你愿意帮助我们并且阻止这些血精灵的话，冲突现在正在哈剌的西北方发生。血精灵们和阿古斯之手的士兵正在战斗!加入他们并且让他们知道我们的厉害!', '审问者试图查明这些血精灵正在纳葛兰这里做些什么。从我听到的，他们在追踪来自欧夏刚，西方的钻石山，的水晶粉末残渣。无疑地要用在一些阴险的计画或者混合物里的……$B$B假如你愿意帮助我们并且阻止这些血精灵的话，冲突现在正在哈剌的西北方发生。血精灵们和阿古斯之手的士兵正在战斗!加入他们并且让他们知道我们的厉害!', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9739, '德莱尼回来了!当他们正在外巡逻西方的道路时，这个小组偷袭我最好的两名狼骑士。不用多说，我们在那次冲突中胜利了。$B$B现在我不能确定到底发生了什么事，但我的斥候告诉我，血精灵和德莱尼遍及整个哈剌。我不是血精灵爱好者，但德莱尼却是我所厌恶的。如果你在哈剌看见德莱尼或者他们的盟友，一定要为我把他们狠狠的教训一顿。$B$B哈剌的位置是这里的西方……如果你现在要去的话。', '德莱尼回来了!当他们正在外巡逻西方的道路时，这个小组偷袭我最好的两名狼骑士。不用多说，我们在那次冲突中胜利了。$B$B现在我不能确定到底发生了什么事，但我的斥候告诉我，血精灵和德莱尼遍及整个哈剌。我不是血精灵爱好者，但德莱尼却是我所厌恶的。如果你在哈剌看见德莱尼或者他们的盟友，一定要为我把他们狠狠的教训一顿。$B$B哈剌的位置是这里的西方……如果你现在要去的话。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (9749, '我们已经控制了双塔废墟!快前往保卫它!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
