INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10229, '你好，我是维序派的萨义德上尉，我身旁的这些士兵是我的复仇者部队。我们等待着摧毁虚空管道的那个肉人的命令。用那个肉人的话来说，我们要赶到迪门修斯的巢穴外，等待最后进攻的命令！$B$B等了这么多年……虚空领主很快就会面对我们的夹击了！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
