INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10233, '欢迎来到秘境领地，旅游者。我要警告你谨慎处理那个地精，休利。我相信我之前从没听过任何人使用这个字「哥们」作为标点符号。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
