INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10363, '我不能相信他们将我的传送器移到镇外!大部分被意外影响的地精都在几周内就会恢复成某种人型，而能够参与这样一个伟大的地精实验是一种荣誉!$B$B除此之外，我们需要蛋!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10365, 'With the help of my brother Zap in Everlook I have constructed a Dimensional Imploder right here in Area 52.   Who needs a rocket when you have instant transportation via Goblin Engineering!   Are you a skilled enough Goblin Engineer to lean how to build your own Imploder beacon?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
