INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10379, '沙塔斯城有两座银行，它们都座落在城市中心的边缘上。一座由奥尔多的德莱尼人运作着，而另一座属于血精灵占星者。', '沙塔斯城有两座银行，它们都座落在城市中心的边缘上。一座由奥尔多的德莱尼人运作着，而另一座属于血精灵占星者。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
