INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (10466, '', 'When you run with Razak\'s Roughriders, you learn a thing or two about following orders.$B$BThe first thing is that you fight until you die.  The second thing is, see the first thing.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
