INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11324, '喂喂！我们被那群该死的海盗给堵在海湾里了！$B$B朝他们丢炸弹，看能不能帮我们出去？$B$B我们偷来的飞艇就在那里……我都听见它的声音了！趁它起航之前跳上去！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11328, '我是个囚犯！$B$B这帮混蛋把我美丽的飞艇从天上打了下来。你知道这是为什么吗？就因为我帮部落运输了一些士兵和补给物资！这算什么新鲜事嘛！真是的！$B$B现在他们逼我开着飞艇在海湾上巡逻，帮他们轰炸海盗。现在飞艇的零件短缺得厉害，她随时都有坠毁的危险！我几乎靠口水和绳子才能保证她不散架！$B$B你要是出了什么事，我可不负责，不过你最好趁她起航前跳上去。$B$B我跟你说，这是违法的！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11330, '我是个囚犯！$B$B这帮混蛋把我美丽的飞艇从天上打了下来。你知道这是为什么吗？就因为我帮部落运输了一些士兵和补给物资！这算什么新鲜事嘛！真是的！$B$B现在他们逼我开着飞艇在海湾上巡逻，帮他们轰炸海盗。现在飞艇的零件短缺得厉害，她随时都有坠毁的危险！我几乎靠口水和绳子才能保证她不散架！$B$B如果你愿意帮忙的话，就去那边候着吧。她在1分钟之内就会入港。$B$B我跟你说，这是违法的！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11332, '我是个囚犯！$B$B这帮混蛋把我美丽的飞艇从天上打了下来。你知道这是为什么吗？就因为我帮部落运输了一些士兵和补给物资！这算什么新鲜事嘛！真是的！$B$B现在他们逼我开着飞艇在海湾上巡逻，帮他们轰炸海盗。现在飞艇的零件短缺得厉害，她随时都有坠毁的危险！我几乎靠口水和绳子才能保证她不散架！$B$B如果你愿意帮忙的话，就去那边候着吧。她在$3078w分钟之内就会入港。$B$B我跟你说，这是违法的！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11338, '拿出你的耐心来，$N。我可不是靠着仓促行事而赢得霸德·奈德瑞克这个名号的。$B$B我已经在做最后的准备，而不久之后祖阿曼的宝藏和秘密都将属於我的了!$B$B<霸德停止说话并且看了你一下，然后轻笑着。>$B$B…当然，我是说我们的了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11352, '我的工作就是日夜不停地制造弹药或是给弓换弦。$B$B你需要帮助吗？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11354, 'Listen, $c, it\'s clear that you and your friends know your way around Azeroth. Surely you\'ve come across certain folks who hunger for fame and fortune. $b$bSend me anyone who might be willing to enter Zul\'Aman and I\'ll make it worth your while.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
