INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11358, '他逃走了？万岁！', '他逃走了？万岁！', '无头骑士很可怕，但我一点都不害怕！一点也不害怕！', '无头骑士很可怕，但我一点都不害怕！一点也不害怕！', '无头骑士落荒而逃了！', '无头骑士落荒而逃了！', '要是他回来了，你会再去和他战斗吗？', '要是他回来了，你会再去和他战斗吗？', '我希望我能有个像你一样的$G爸爸:妈妈;！', '我希望我能有个像你一样的$G爸爸:妈妈;！', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11359, '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '万鬼节是个恐怖的节日，但是我喜欢!', '无头骑士住在血色修道院。有时候他会出来吓吓大人们，不过不是我！', '无头骑士住在血色修道院。有时候他会出来吓吓大人们，不过不是我！', '我以前跟旅店要到一些糖果。我等不及去要更多的糖了！', '我以前跟旅店要到一些糖果。我等不及去要更多的糖了！', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11362, '在远古魔法的作用下，这场森林大火绵延燃烧了数年都不曾熄灭。这里本是始祖龙女王及其配偶的栖息地。$B$B然而，某个早已被遗忘的邪恶种族已经觉醒，并企图再次奴役始祖龙女王的后裔，让它们为自己服务。$B$B这些维库人最近在灰烬龙巢附近频频滋事，严重威胁到你们的安危。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11365, '此版面是给泥链营地的热砂代表们张贴赏金和工作告示之用。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11370, '没错!他们已经具备了所有我能招集到的人员应有的长处。我简直无法想像事情会出错。$B$B这里有些东西，$C，挑一件吧。我已经改进它了─其实只有改良几个小地方罢了。现在我们已经没有理由会失败了!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
