INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11436, '亚当斯上尉派我来调查哈尔格林德到底发生了什么事。我以这座被废弃的维库人哨塔为掩护，费了好大的工夫，最多只能靠到这么近了。$B$B被遗忘者一定在秘密策划着什么。我感觉得到！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
