INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11945, '嗨你好!只因为我比任何人都会竞速山羊，不代表我无法出力帮助酒厂。如果你喜欢的话可以帮忙。$B$B不过等等，还有别的!$B$B帮忙啤酒节的事宜代表你有机会可以骑乘各种族中最迅捷的山羊!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11947, '啊哈哈，你有看到吗?格利夫塔回来了!回到我的摊位，带着我的旧东西，准备好继续跟你做生意!$B$B想要变得更好，更强壮，更迅速吗?这些护符的每一个都保证如我所说的作用一般。过来买一个奇妙的玩意儿吧!物超所值喔!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11956, '黑暗的时代成就了不可思议的友谊。也许值得到旧洛丹伦一游，观看被遗忘者焚烧他们的小稻草人。', '黑暗的时代成就了不可思议的友谊。也许值得到旧洛丹伦一游，观看被遗忘者焚烧他们的小稻草人。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11957, '我了解这个节庆不是我们盟友创造的，而是整个部落。我们为什么要跟着一起做，我真的不知道。', '我了解这个节庆不是我们盟友创造的，而是整个部落。我们为什么要跟着一起做，我真的不知道。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
