INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11960, '我说，免费的糖果真是个赔钱的买卖。呃……或许它们只是招揽顾客的减价货呢……糖果诱使你进入旅店，接下来你就要花钱吃肉啦。太棒了！', '我说，免费的糖果真是个赔钱的买卖。呃……或许它们只是招揽顾客的减价货呢……糖果诱使你进入旅店，接下来你就要花钱吃肉啦。太棒了！', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
