INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11962, '去庆祝另一个该死的敌人诞生是件很怪的事，至少可以这么说。不过，我们还是会於参与我们盟友他们的庆典。', '去庆祝另一个该死的敌人诞生是件很怪的事，至少可以这么说。不过，我们还是会於参与我们盟友他们的庆典。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11963, '被遗忘者庆祝他们的自由是对的。还有什么更重要呢?', '被遗忘者庆祝他们的自由是对的。还有什么更重要呢?', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11964, '被遗忘者每晚都会焚毁一个巨大的稻草人，真不明白这样做的意义何在，不过我们的仪式在他们眼中应该也很奇怪。', '被遗忘者每晚都会焚毁一个巨大的稻草人，真不明白这样做的意义何在，不过我们的仪式在他们眼中应该也很奇怪。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (11966, '我们在万鬼节期间庆祝这个由女妖之王将我们从巫妖王及天谴军团手中解放出来的日子。一定要参加每晚在幽暗城外举行的稻草人节。', '我们在万鬼节期间庆祝这个由女妖之王将我们从巫妖王及天谴军团手中解放出来的日子。一定要参加每晚在幽暗城外举行的稻草人节。', '很高兴见到你 $N', '很高兴见到你 $N', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12044, '我的顾客寻找的多是稀有且珍贵的物品。$B$B你可以是那个将东西带来给我的人，$C。我提供的报酬会是你所渴望的事物。$B$B我们要不要多谈些今天我想要取得的东西呢?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
