INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12332, '我现在很忙。', '我现在很忙。', '有点忙。', '我现在很忙。', '我现在很忙。', '我现在很忙。', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12333, '', 'The flight from Grom\'gol to Undercity is beautiful, though uneventful.  Though that is no excuse to fall asleep on the job.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12334, '经过了灰烬与污泥。让你自己风乾一阵子吧。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12335, '牛头大佬在我替他们安排下一场巡回演出时都会待在银月城里。他们没排练的时候通常会在我上方的楼台里消磨时间。放心挥手吧...他们喜欢接受注目。$B$B我们目前没有接受表演或是工作人员，不过你以后可以常来连系看看。谁知道会发生什么事呢?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12336, '你没看过他们在泰罗卡森林的表演吗？太精彩了！', '你没看过他们在泰罗卡森林的表演吗？太精彩了！', '牛头人酋长乐队一定是整个艾泽拉斯最著名的乐队了！', '牛头人酋长乐队一定是整个艾泽拉斯最著名的乐队了！', '我想知道他们会在银月城待多久，真希望他们能在这里表演！', '我想知道他们会在银月城待多久，真希望他们能在这里表演！', '如果可以的话，我愿付出一切来找寻一个上台和他们谈话的机会……', '如果可以的话，我愿付出一切来找寻一个上台和他们谈话的机会……', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12338, '', 'Do not fight the enemy unprepared, $N.  I can provide you with very powerful items... provided you have proven yourself to us.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12339, '凯尔的宠物是该在羞辱中学上一课。我们将会击溃曦刃军团并且从他们手中夺下日境港。$B$B我们目前的目标达成率为$3238w％。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12340, '日境属於我们了。凯尔的走狗四散奔逃...是时候来终结他们的苦难了!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12341, '我负责管理支援牛头大佬的工作人员。$B$B你知道的:打包，开车，还有乐器拆卸，舞台搭盖及拆掉，设置烟火，所有这种类的事。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12342, '这里的史怀哲是我的老朋友...那，是一件贵重的东西。我是说，友情。我想我会忘掉这种感觉，直到我们再度在撒塔斯城遇见。$B$B这些日子他没有常常待在提瑞斯法，所以我想我会使用这里的传送之门去传达我的问候。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
