INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12350, '$R，你已经掌握了与我们交流的诀窍，不过这算不了什么。$B$B水塘那边还有大群的冬鳞背叛者等着你去收拾呢。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12352, '我又不是瞎子。我能看到真实的情况！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12357, '不久前，我来这里观察冬鳞鱼人。我伪装成他们的模样，保持着一定的距离，记录鱼人的日常生活情况。$B$B没过多久，一切都改变了。$B$B考达拉的一场地震引发山崩，附近的洞穴都露了出来。居住在洞穴中的龙虾人开始偷窃冬鳞蝌蚪。鱼人们试图拯救他们的后代，但是一旦他们进入洞穴，就像被转换了心智似的，沦为了龙虾人的走狗。$B$B那些尚未堕落的鱼人撤退到这里，我勉为其难地成为了他们的首领。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
