INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12362, 'Glug bmrgl grggl！', '', 'Mawurrgggl, gurgl lur！', '', 'Glumrggl？', '', 'Brggl-brggl Mrgl-Mrgl！Gluk！', '', 'Blrmrrgl gulrgl grr。', '', 'RAWGRLRLRLRRLGLRL！！！', '', 'Mrglrglrglrgl... glrgl？', '', 'Mrglmrgl Glrggl！', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12363, '没时间闲聊——我有工作要做！', '', '你能听懂我说的话？我能听懂你说的！', '', '$R，我为什么要和你说话？这堆烂摊子很可能就是拜你们所赐！', '', '我们要忙着收集贝壳和消灭逆戟鲸呢。你要么来帮忙，要么就别挡路！', '', 'Blurglgr murbrgl……开个玩笑，我能听懂你的话。', '', '你查明村落里的鱼人发狂的原因了吗？', '', '我就知道我们不应该进入洞穴。那里的状况太异常了！', '', '总觉得新国王姆嘎姆嘎有什么地方不对劲，但是又说不上来是哪儿有问题', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12364, '我是阿图伊克，卡玛古的卡鲁亚克氏族酋长。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12370, '该死的凯尔萨斯和他的走狗们！我们的一切都没有了，但他们竟然愚蠢到认为我们不会反击。女伯爵莉亚德琳已经给我们找到了一种新的能量源。我们不会坐以待毙。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
