INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12658, '嗨！菲兹兰克的飞行员都是王牌中的王牌呀。坚持训练是我们保持领先的秘诀。这其中就包括了跳伞训练。你永远也不知道什么时候会用到降落伞，但是迟早你会用上的。$B$B因此，我们设置了通往塔顶的传送装置。如果你是真正的士兵，就勇敢地拉开降落伞跳下来吧。塔顶有一位军官，负责提供降落伞包。$B$B$C，想要接受训练的话，我就教你如何使用传送装置。喏，你只需要踩在上面就是了。', '嗨！菲兹兰克的飞行员都是王牌中的王牌呀。坚持训练是我们保持领先的秘诀。这其中就包括了跳伞训练。你永远也不知道什么时候会用到降落伞，但是迟早你会用上的。$B$B因此，我们设置了通往塔顶的传送装置。如果你是真正的士兵，就勇敢地拉开降落伞跳下来吧。塔顶有一位军官，负责提供降落伞包。$B$B$C，想要接受训练的话，我就教你如何使用传送装置。喏，你只需要踩在上面就是了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
