INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12687, '欢迎你，$C！希望你没有恐高症！$B$B我为你准备了练习用的降落伞。它们是在紧急着陆时使用的。如果你想要缓缓落地，还不如骑着坐骑随便蹦两下呢。如果你想体验极速快感，就要从高处跳下来，在快到落地时打开降落伞！动作要快！你的反应够快吗？$B$B降落伞的效果只能持续几秒，因此你必须在适当的时机使用它。不知道你能不能掌握其中的诀窍。希望你成功。', '欢迎你，$C！希望你没有恐高症！$B$B我为你准备了练习用的降落伞。它们是在紧急着陆时使用的。如果你想要缓缓落地，还不如骑着坐骑随便蹦两下呢。如果你想体验极速快感，就要从高处跳下来，在快到落地时打开降落伞！动作要快！你的反应够快吗？$B$B降落伞的效果只能持续几秒，因此你必须在适当的时机使用它。不知道你能不能掌握其中的诀窍。希望你成功。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
