INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12696, '$C，谢谢你，自从收到兄弟的来信后，我就一直在关注这件事。$B$B他的牺牲总算不是徒劳的，愿他的灵魂安息。蓝龙军团会付出惨痛的代价的。$B$B我等着这一天！', '$C，谢谢你，自从收到兄弟的来信后，我就一直在关注这件事。$B$B他的牺牲总算不是徒劳的，愿他的灵魂安息。蓝龙军团会付出惨痛的代价的。$B$B我等着这一天！', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12698, '士兵，阻止天灾军团的行动！随时向我汇报情况。一旦你发现可疑的人物，不管是谁，先杀了再说！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12699, '记住我说过的话，这帮愚蠢的被遗忘者迟早会毁了我们！', '', '你问我的意见？我觉得我们应该趁早杀掉所有的药剂师！', '', '部落是为荣耀与战斗而生的。这帮药剂师肯定在干什么不光彩的勾当。', '', '我始终留意着这群白痴的一举一动。真是怎么提防都不为过！', '', '如果你要和那些医师打交道的话，千万当心。他们一定在干什么不正常的勾当。', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12701, '朋友，西边那片荒野上都是尸体。如果你还想活命的话，就离开这里！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12703, '我在阿格玛之锤的工作远不止照料兽栏里的动物这么简单。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
